import React, { useEffect, useRef, useState } from "react";
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import axios from 'axios';
import 'leaflet/dist/leaflet.css';
import '../css/DetailsRapport.css';

const API = process.env.REACT_APP_API_URL;

const DetailsRapport = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { authData } = useAuth();
  
  const [rapport, setRapport] = useState(null);
  const [metaData, setMetaData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [hasAccess, setHasAccess] = useState(false);
  const [accessChecked, setAccessChecked] = useState(false);
  const mapRef = useRef(null);
  const leafletMapRef = useRef(null);

  // Vérification d'accès et chargement des données
  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        setError('');

        console.log('📥 Chargement des données du rapport pour les détails...');

        // Utiliser la même API que ModifierRapport
        const [rapportRes, accesResponse] = await Promise.all([
          axios.get(`${API}/rapports/${id}`),
          axios.get(`${API}/rapports/${id}/acces`)
        ]);

        console.log('📄 Données du rapport récupérées :', rapportRes.data);

        const rapportData = rapportRes.data.rapport || rapportRes.data;
        const metaDataReceived = rapportRes.data.metaData || {};
        const accesList = accesResponse.data;

        // Vérifier les accès
        const estCreateur = rapportData.id_operateur === authData.Opid;
        const aAccess = accesList.some(acc => acc.id_operateur === authData.Opid);
        setHasAccess(estCreateur || aAccess);

        // Stocker les données
        setRapport(rapportData);
        setMetaData(metaDataReceived);

        console.log('✅ Données chargées avec succès');
      } catch (err) {
        console.error('❌ Erreur lors du chargement des données :', err);
        setError('Impossible de charger les détails du rapport.');
        setHasAccess(false);
      } finally {
        setLoading(false);
        setAccessChecked(true);
      }
    };

    if (id && authData?.Opid) {
      fetchData();
    }
  }, [id, authData]);

  // Ajout : fonction pour charger Leaflet si besoin
  const loadLeaflet = () => {
    return new Promise((resolve) => {
      if (window.L) {
        resolve();
      } else {
        const script = document.createElement('script');
        script.src = "https://unpkg.com/leaflet/dist/leaflet.js";
        script.onload = resolve;
        document.body.appendChild(script);
      }
    });
  };

  useEffect(() => {
    let isMounted = true;
    async function initMap() {
      if (
        mapRef.current &&
        rapport &&
        rapport.latitude &&
        rapport.longitude
      ) {
        await loadLeaflet();
        if (!isMounted) return;
        
        // Nettoyer la carte précédente si elle existe
        if (leafletMapRef.current) {
          leafletMapRef.current.remove();
        }
        
        const lat = parseFloat(rapport.latitude);
        const lng = parseFloat(rapport.longitude);
        
        // Petit délai pour s'assurer que le conteneur est visible
        setTimeout(() => {
          if (!isMounted || !mapRef.current) return;
          
          leafletMapRef.current = window.L.map(mapRef.current, { 
            zoomControl: false, 
            dragging: false, 
            scrollWheelZoom: false, 
            doubleClickZoom: false, 
            boxZoom: false, 
            keyboard: false, 
            tap: false, 
            touchZoom: false 
          }).setView([lat, lng], 10);
          
          window.L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
            attribution: '&copy; OpenStreetMap contributors',
            maxZoom: 18
          }).addTo(leafletMapRef.current);
          
          window.L.marker([lat, lng]).addTo(leafletMapRef.current);
          
          // Désactive toutes les interactions
          leafletMapRef.current.dragging.disable();
          leafletMapRef.current.touchZoom.disable();
          leafletMapRef.current.doubleClickZoom.disable();
          leafletMapRef.current.scrollWheelZoom.disable();
          leafletMapRef.current.boxZoom.disable();
          leafletMapRef.current.keyboard.disable();
          if (leafletMapRef.current.tap) leafletMapRef.current.tap.disable();
          
          // IMPORTANT : Force le redimensionnement de la carte
          setTimeout(() => {
            if (leafletMapRef.current && isMounted) {
              leafletMapRef.current.invalidateSize();
            }
          }, 100);
          
        }, 100);
      }
    }
    initMap();
    return () => {
      isMounted = false;
      if (leafletMapRef.current) {
        leafletMapRef.current.remove();
        leafletMapRef.current = null;
      }
    };
  }, [rapport]);

  // Fonction pour formater la date
  const formatDate = (dateString) => {
    if (!dateString) return 'Non spécifiée';
    const date = new Date(dateString);
    return date.toLocaleDateString('fr-FR', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  // Fonction pour formater les booléens
  const formatBoolean = (value) => {
    if (value === true || value === 1) return 'Oui';
    if (value === false || value === 0) return 'Non';
    return 'Non spécifié';
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Chargement des détails du rapport...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="error-container">
        <h2>Erreur</h2>
        <p>{error}</p>
        <button className="btn-primary" onClick={() => navigate('/liste-rapports')}>
          Retour à la liste des rapports
        </button>
      </div>
    );
  }

  if (!accessChecked) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Vérification des permissions...</p>
      </div>
    );
  }

  if (!hasAccess) {
    return (
      <div className="error-container">
        <h2>Accès refusé</h2>
        <p>Vous n'avez pas les droits pour consulter ce rapport.</p>
        <button className="btn-primary" onClick={() => navigate('/liste-rapports')}>
          Retour à la liste des rapports
        </button>
      </div>
    );
  }

  if (!rapport) {
    return (
      <div className="error-container">
        <h2>Rapport non trouvé</h2>
        <p>Le rapport demandé n'existe pas ou n'est plus disponible.</p>
        <button className="btn-primary" onClick={() => navigate('/liste-rapports')}>
          Retour à la liste des rapports
        </button>
      </div>
    );
  }

  return (
    <div className="details-rapport-container">
      <div className="details-header">
        <h1>Détails du Rapport</h1>
        <div className="header-actions">
          <button 
            className="btn-secondary" 
            onClick={() => navigate('/liste-rapports')}
          >
            ← Retour à la liste
          </button>
          {hasAccess && (
            <button 
              className="btn-primary" 
              onClick={() => navigate(`/modifier-rapport/${id}`)}
            >
              ✏️ Modifier
            </button>
          )}
        </div>
      </div>

      <div className="details-content">
        {/* Section Informations Générales */}
        <div className="details-section">
          <h2>Informations Générales</h2>
          <div className="details-grid">
            <div className="detail-item">
              <label>Titre :</label>
              <span>{rapport.titre || 'Non spécifié'}</span>
            </div>
            <div className="detail-item">
              <label>Date de l'événement :</label>
              <span>{formatDate(rapport.date_evenement)}</span>
            </div>
            <div className="detail-item">
              <label>Type d'événement :</label>
              <span>{rapport.type_evenement_libelle || 'Non spécifié'}</span>
            </div>
            <div className="detail-item">
              <label>Sous-type d'événement :</label>
              <span>{rapport.sous_type_evenement_libelle || 'Non spécifié'}</span>
            </div>
            <div className="detail-item">
              <label>Origine de l'événement :</label>
              <span>{rapport.origine_evenement_libelle || 'Non spécifié'}</span>
            </div>
            <div className="detail-item full-width">
              <label>Description :</label>
              <span>{rapport.description_globale || 'Aucune description'}</span>
            </div>
          </div>
        </div>

        {/* Section Cible */}
        {metaData?.cible && (
          <div className="details-section">
            <h2>Cible de l'Événement</h2>
            <div className="details-grid">
              <div className="detail-item">
                <label>Type de cible :</label>
                <span>{rapport.type_cible_libelle || 'Non spécifié'}</span>
              </div>
              <div className="detail-item">
                <label>Nom de la cible :</label>
                <span>{rapport.nom_cible || metaData.cible.nom || 'Non spécifié'}</span>
              </div>
              <div className="detail-item">
                <label>Pavillon :</label>
                <span>{rapport.pavillon_cible || metaData.cible.pavillon || 'Non spécifié'}</span>
              </div>
              <div className="detail-item">
                <label>Immatriculation :</label>
                <span>{rapport.immatriculation || metaData.cible.immatriculation || 'Non spécifié'}</span>
              </div>
              <div className="detail-item">
                <label>Type de produit :</label>
                <span>{rapport.TypeProduit || metaData.cible.TypeProduit || 'Non spécifié'}</span>
              </div>
              <div className="detail-item">
                <label>Quantité de produit :</label>
                <span>{rapport.QuantiteProduit || metaData.cible.QuantiteProduit || 'Non spécifié'}</span>
              </div>
            </div>
          </div>
        )}

        {/* Section Localisation */}
        {metaData?.localisation && (
          <div className="details-section">
            <h2>Localisation</h2>
            <div className="details-grid">
              <div className="detail-item">
                <label>Zone géographique :</label>
                <span>{rapport.nom_zone_lieu || metaData.localisation.nom_zone_lieu || 'Non spécifiée'}</span>
              </div>
              <div className="detail-item full-width">
                <label>Détails du lieu :</label>
                <span>{metaData.localisation.details_lieu || 'Non spécifié'}</span>
              </div>
              <div className="detail-item">
                <label>Latitude :</label>
                <span>{metaData.localisation.latitude || 'Non spécifiée'}</span>
              </div>
              <div className="detail-item">
                <label>Longitude :</label>
                <span>{metaData.localisation.longitude || 'Non spécifiée'}</span>
              </div>
            </div>
          </div>
        )}

        {/* Section Météo */}
        {metaData?.meteo && (
          <div className="details-section">
            <h2>Conditions Météorologiques</h2>
            <div className="details-grid">
              <div className="detail-item">
                <label>Direction du vent :</label>
                <span>{metaData.meteo.direction_vent || 'Non spécifiée'}</span>
              </div>
              <div className="detail-item">
                <label>Force du vent :</label>
                <span>{metaData.meteo.force_vent || 'Non spécifiée'}</span>
              </div>
              <div className="detail-item">
                <label>État de la mer :</label>
                <span>{metaData.meteo.etat_mer || 'Non spécifié'}</span>
              </div>
              <div className="detail-item">
                <label>Nébulosité :</label>
                <span>{metaData.meteo.nebulosite || 'Non spécifiée'}</span>
              </div>
              <div className="detail-item">
                <label>Marée :</label>
                <span>{metaData.meteo.maree || 'Non spécifiée'}</span>
              </div>
            </div>
          </div>
        )}

        {/* Section Alertes */}
        {metaData?.alertes && (
          <div className="details-section">
            <h2>Contacts et Alertes</h2>
            <div className="details-grid">
              <div className="detail-item">
                <label>CEDRE alerté :</label>
                <span>{formatBoolean(metaData.alertes.cedre || metaData.alertes.cedre_alerte)}</span>
              </div>
              <div className="detail-item">
                <label>CROSS alerté :</label>
                <span>{formatBoolean(metaData.alertes.cross_contact || metaData.alertes.cross_alerte)}</span>
              </div>
              <div className="detail-item">
                <label>SMP :</label>
                <span>{formatBoolean(metaData.alertes.smp)}</span>
              </div>
              <div className="detail-item">
                <label>BSAA :</label>
                <span>{formatBoolean(metaData.alertes.bsaa)}</span>
              </div>
              <div className="detail-item">
                <label>POLREP :</label>
                <span>{formatBoolean(metaData.alertes.polrep)}</span>
              </div>
              <div className="detail-item">
                <label>Photo :</label>
                <span>{formatBoolean(metaData.alertes.photo)}</span>
              </div>
              <div className="detail-item">
                <label>Dérive MOTHY :</label>
                <span>{formatBoolean(metaData.alertes.derive_mothym || metaData.alertes.derive_mothy)}</span>
              </div>
              <div className="detail-item">
                <label>POLMAR Terre :</label>
                <span>{formatBoolean(metaData.alertes.pne || metaData.alertes.polmar_terre)}</span>
              </div>
              {metaData.alertes.delai_appareillage_bsaa && (
                <div className="detail-item">
                  <label>Délai d'appareillage :</label>
                  <span>{formatDate(metaData.alertes.delai_appareillage_bsaa)}</span>
                </div>
              )}
              {metaData.alertes.moyen_proximite && (
                <div className="detail-item full-width">
                  <label>Moyens à proximité :</label>
                  <span>{metaData.alertes.moyen_proximite}</span>
                </div>
              )}
              {metaData.alertes.moyen_depeche && (
                <div className="detail-item full-width">
                  <label>Moyens dépêchés :</label>
                  <span>{metaData.alertes.moyen_depeche}</span>
                </div>
              )}
              {metaData.alertes.moyen_marine_etat && (
                <div className="detail-item full-width">
                  <label>Moyens maritimes/État :</label>
                  <span>{metaData.alertes.moyen_marine_etat}</span>
                </div>
              )}
              {metaData.alertes.risque_court_terme && (
                <div className="detail-item full-width">
                  <label>Risque court terme :</label>
                  <span>{metaData.alertes.risque_court_terme}</span>
                </div>
              )}
              {metaData.alertes.risque_moyen_long_terme && (
                <div className="detail-item full-width">
                  <label>Risque moyen/long terme :</label>
                  <span>{metaData.alertes.risque_moyen_long_terme}</span>
                </div>
              )}
            </div>
          </div>
        )}

        {/* Section Informations de suivi */}
        <div className="details-section">
          <h2>Informations de Suivi</h2>
          <div className="details-grid">
            <div className="detail-item">
              <label>Créé par :</label>
              <span>{rapport.operateur_createur_nom_complet || 'Inconnu'}</span>
            </div>
            <div className="detail-item">
              <label>Date de création :</label>
              <span>{formatDate(rapport.date_creation)}</span>
            </div>
            {rapport.operateur_modificateur_nom_complet && (
              <>
                <div className="detail-item">
                  <label>Modifié par :</label>
                  <span>{rapport.operateur_modificateur_nom_complet}</span>
                </div>
                <div className="detail-item">
                  <label>Date de modification :</label>
                  <span>{formatDate(rapport.date_modification)}</span>
                </div>
              </>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default DetailsRapport;
