import React, { useEffect, useState } from "react";
import axios from "axios";

const Password = () => {
    const [passwords, setPasswords] = useState([]);
    const [visibleIds, setVisibleIds] = useState([]);
    const [editId, setEditId] = useState(null);
    const [editValue, setEditValue] = useState("");

    useEffect(() => {
        fetchPasswords();
    }, []);

    const fetchPasswords = async () => {
        try {
            const res = await axios.get("/api/passwords"); 
            setPasswords(res.data);
        } catch (err) {
            console.error("Erreur lors de la récupération des mots de passe", err);
        }
    };

    const toggleVisibility = (id) => {
        setVisibleIds((prev) =>
            prev.includes(id) ? prev.filter((v) => v !== id) : [...prev, id]
        );
    };

    const handleEdit = (id, value) => {
        setEditId(id);
        setEditValue(value);
    };

    const handleEditChange = (e) => {
        setEditValue(e.target.value);
    };

    const saveEdit = async (id) => {
        try {
            await axios.put(`/api/passwords/${id}`, { password: editValue });
            setPasswords((prev) =>
                prev.map((item) =>
                    item.id === id ? { ...item, password: editValue } : item
                )
            );
            setEditId(null);
            setEditValue("");
        } catch (err) {
            console.error("Erreur lors de la modification du mot de passe", err);
        }
    };

    return (
        <div>
            <h2>Gestion des mots de passe</h2>
            <ul>
                {passwords.map((item) => (
                    <li key={item.id} style={{ marginBottom: 16 }}>
                        {editId === item.id ? (
                            <>
                                <input
                                    type="text"
                                    value={editValue}
                                    onChange={handleEditChange}
                                />
                                <button onClick={() => saveEdit(item.id)}>Enregistrer</button>
                                <button onClick={() => setEditId(null)}>Annuler</button>
                            </>
                        ) : (
                            <>
                                <input
                                    type={visibleIds.includes(item.id) ? "text" : "password"}
                                    value={item.password}
                                    readOnly
                                    style={{ marginRight: 8 }}
                                />
                                <button onClick={() => toggleVisibility(item.id)}>
                                    {visibleIds.includes(item.id) ? "Masquer" : "Afficher"}
                                </button>
                                <button
                                    style={{ marginLeft: 8 }}
                                    onClick={() => handleEdit(item.id, item.password)}
                                >
                                    Éditer
                                </button>
                            </>
                        )}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Password;
