import React, { useEffect, useState } from "react";
import axios from "axios";

const Password = () => {
    const [passwords, setPasswords] = useState([]);
    const [visible, setVisible] = useState(false);
    const [editId, setEditId] = useState(null);
    const [editValue, setEditValue] = useState("");

    const API_BASE_URL = process.env.REACT_APP_API_URL;

    const fetchPasswords = async () => {
        try {
            const res = await axios.get(`${API_BASE_URL}/admin/motdepasse`);
            setPasswords(res.data);
        } catch (err) {
            console.error("Erreur lors de la récupération des mots de passe", err);
        }
    };

    useEffect(() => {
        fetchPasswords();
    }, []);

    const toggleVisibility = () => {
        setVisible((prev) => !prev);
    };

    const handleEdit = (id, value) => {
        setEditId(id);
        setEditValue(value);
    };

    const handleEditChange = (e) => {
        setEditValue(e.target.value);
    };

    const saveEdit = async (id) => {
        try {
            await axios.put(
                `${API_BASE_URL}/admin/motdepasse/${id}`,
                { mot_de_passe: editValue }
            );
            setPasswords((prev) =>
                prev.map((item) =>
                    item.id === id ? { ...item, mot_de_passe: editValue } : item
                )
            );
            setEditId(null);
            setEditValue("");
        } catch (err) {
            console.error("Erreur lors de la modification du mot de passe", err);
        }
    };

    return (
        <div className="passwords-container">
            <button
                className="password-btn password-btn-toggle"
                onClick={toggleVisibility}
                style={{ marginBottom: 16 }}
            >
                {visible ? "Masquer tous" : "Afficher tous"}
            </button>
            <ul className="passwords-list">
                {passwords.map((item) => (
                    <li key={item.id} className="password-item">
                        {editId === item.id ? (
                            <>
                                <h2>{item.info}</h2>
                                <div className="password-edit-row">
                                    <input
                                        className="password-input"
                                        type="text"
                                        value={editValue}
                                        onChange={handleEditChange}
                                    />
                                    <button className="password-btn password-btn-save" onClick={() => saveEdit(item.id)}>
                                        Enregistrer
                                    </button>
                                    <button className="password-btn password-btn-cancel" onClick={() => setEditId(null)}>
                                        Annuler
                                    </button>
                                </div>
                            </>
                        ) : (
                            <>
                                <h2>{item.info}</h2>
                                <div className="password-row">
                                    <span className="password-value">
                                        {visible
                                            ? item.mot_de_passe
                                            : "••••••••"}
                                    </span>
                                    <button
                                        className="password-btn password-btn-edit"
                                        style={{ marginLeft: 8 }}
                                        onClick={() => handleEdit(item.id, item.mot_de_passe)}
                                    >
                                        Éditer
                                    </button>
                                </div>
                            </>
                        )}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Password;
