import { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import {
    Users,
    Key,
    BarChart3,
    Settings,
    LogOut,
    Plus,
    Trash2,
    Eye,
    Shield,
    UserCheck,
    Database,
    EyeOff,
    Edit,
    Save,
    X
} from 'lucide-react';

import '../css/AdminDashboard.css';

const AdminDashboard = () => {
    const { isAdmin, isAuthenticated, logout, operateurNom } = useAuth();
    const navigate = useNavigate();
    const [activeTab, setActiveTab] = useState('dashboard');
    const [stats, setStats] = useState({});
    const [passwords, setPasswords] = useState([]);
    const [operateurs, setOperateurs] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    const API = process.env.REACT_APP_API_URL;

    // Vérifications de sécurité
    useEffect(() => {
        if (!isAuthenticated) {
            navigate('/auth');
            return;
        }
        if (!isAdmin) {
            navigate('/');
            return;
        }
    }, [isAuthenticated, isAdmin, navigate]);

    // Charger les données initiales
    useEffect(() => {
        if (isAdmin && isAuthenticated) {
            loadStats();
            loadPasswords();
            loadOperateurs();
        }
    }, [isAdmin, isAuthenticated]);

    const loadStats = async () => {
        try {
            const response = await axios.get(`${API}/auth/stats`);
            if (response.data.success) {
                setStats(response.data.data);
            }
        } catch (err) {
            console.error('Erreur lors du chargement des statistiques:', err);
        }
    };

    const loadPasswords = async () => {
        try {
            const response = await axios.get(`${API}/auth/passwords`);
            if (response.data.success) {
                setPasswords(response.data.data);
            }
        } catch (err) {
            console.error('Erreur lors du chargement des mots de passe:', err);
        }
    };

    const loadOperateurs = async () => {
        try {
            const response = await axios.get(`${API}/auth/operateurs`);
            if (response.data.success) {
                setOperateurs(response.data.data[0] || []);
            }
        } catch (err) {
            console.error('Erreur lors du chargement des opérateurs:', err);
        }
    };

    const handleLogout = () => {
        logout();
        navigate('/auth');
    };

    if (!isAuthenticated || !isAdmin) {
        return <div className="flex items-center justify-center min-h-screen">Chargement...</div>;
    }

    return (
        <div className="admin-dashboard-root">
            {/* Header */}


            <div className="admin-main">
                {/* Navigation */}
                <nav className="admin-nav">
                    <ul className="admin-nav-list">
                        {[
                            { id: 'dashboard', label: 'Tableau de bord', icon: BarChart3 },
                            { id: 'passwords', label: 'Mots de passe', icon: Key },
                            { id: 'operateurs', label: 'Opérateurs', icon: Users },
                            { id: 'settings', label: 'Paramètres', icon: Settings }
                        ].map(tab => {
                            const Icon = tab.icon;
                            return (
                                <li key={tab.id}>
                                    <button
                                        onClick={() => setActiveTab(tab.id)}
                                        className={`admin-nav-btn ${activeTab === tab.id
                                                ? 'admin-nav-btn-active'
                                                : ''
                                            }`}
                                    >
                                        <Icon className="admin-nav-icon" />
                                        <span>{tab.label}</span>
                                    </button>
                                </li>
                            );
                        })}

                        <li
                            onClick={handleLogout}
                            className="admin-logout-btn"
                        >
                            
                            <button><LogOut className="admin-logout-icon" />Déconnexion</button>
                        </li>
                    </ul>

                </nav>

                <header className="admin-header">
                    <div className="admin-header-container">
                        <div className="admin-header-content">
                            <div className="admin-header-left">
                                
                                <div>
                                    <h1 className="admin-title">Panneau d'Administration</h1>
                                    <p className="admin-welcome">Bienvenue, {operateurNom}</p>
                                </div>
                            </div>

                        </div>
                    </div>
                </header>
                {/* Contenu principal */}
                {activeTab === 'dashboard' && <DashboardTab stats={stats} />}
                {activeTab === 'passwords' && <PasswordsTab passwords={passwords} onReload={loadPasswords} />}
                {activeTab === 'operateurs' && <OperateursTab operateurs={operateurs} onReload={loadOperateurs} />}
                {activeTab === 'settings' && <SettingsTab />}
            </div>


        </div>



    );
};

// Composant Tableau de bord
const DashboardTab = ({ stats }) => (
    <div className="space-y-6">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
            <StatCard
                title="Total Mots de Passe"
                value={stats.totalPasswords || 0}
                icon={Key}
                color="blue"
            />
            <StatCard
                title="Total Opérateurs"
                value={stats.totalOperateurs || 0}
                icon={Users}
                color="green"
            />
            <StatCard
                title="Mots de Passe Admin"
                value={stats.adminPasswords || 0}
                icon={Shield}
                color="red"
            />
            <StatCard
                title="Mots de Passe Opérateur"
                value={stats.operateurPasswords || 0}
                icon={UserCheck}
                color="purple"
            />
        </div>

        <div className="bg-white rounded-lg shadow p-6">
            <h3 className="text-lg font-semibold text-gray-900 mb-4">Activité Récente</h3>
            <div className="text-sm text-gray-500">
                <p>Dernière connexion: {new Date().toLocaleString()}</p>
                <p>Système opérationnel</p>
            </div>
        </div>
    </div>
);

// Composant Carte de Statistique
const StatCard = ({ title, value, icon: Icon, color }) => {
    const colorClasses = {
        blue: 'bg-blue-500 text-blue-600 bg-blue-50',
        green: 'bg-green-500 text-green-600 bg-green-50',
        red: 'bg-red-500 text-red-600 bg-red-50',
        purple: 'bg-purple-500 text-purple-600 bg-purple-50'
    };

    return (
        <div className="bg-white rounded-lg shadow p-6">
            <div className="flex items-center">
                <div className={`p-2 rounded-md ${colorClasses[color].split(' ')[2]}`}>
                    <Icon className={`h-6 w-6 ${colorClasses[color].split(' ')[1]}`} />
                </div>
                <div className="ml-4">
                    <p className="text-sm font-medium text-gray-500">{title}</p>
                    <p className="text-2xl font-semibold text-gray-900">{value}</p>
                </div>
            </div>
        </div>
    );
};

// Composant Gestion des Mots de Passe
const PasswordsTab = ({ passwords, onReload }) => {
    const [showAddForm, setShowAddForm] = useState(false);
    const [newPassword, setNewPassword] = useState({ mot_de_passe: '', info: 'Operateur' });
    const [visiblePasswords, setVisiblePasswords] = useState({});
    const [loading, setLoading] = useState(false);

    const API = process.env.REACT_APP_API_URL;

    const handleAddPassword = async (e) => {
        e.preventDefault();
        setLoading(true);

        try {
            const response = await fetch(`${API}/auth/passwords`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(newPassword)
            });

            if (response.ok) {
                setNewPassword({ mot_de_passe: '', info: 'Operateur' });
                setShowAddForm(false);
                onReload();
            }
        } catch (err) {
            console.error('Erreur lors de l\'ajout:', err);
        } finally {
            setLoading(false);
        }
    };

    const handleDeletePassword = async (id) => {
        if (!window.confirm('Êtes-vous sûr de vouloir supprimer ce mot de passe ?')) return;

        try {
            const response = await fetch(`${API}/auth/passwords/${id}`, {
                method: 'DELETE'
            });

            if (response.ok) {
                onReload();
            }
        } catch (err) {
            console.error('Erreur lors de la suppression:', err);
        }
    };

    const togglePasswordVisibility = (id) => {
        setVisiblePasswords(prev => ({
            ...prev,
            [id]: !prev[id]
        }));
    };

    return (
        <div className="space-y-6">
            <div className="flex justify-between items-center">
                <h3 className="text-lg font-semibold text-gray-900">Gestion des Mots de Passe</h3>
                <button
                    onClick={() => setShowAddForm(true)}
                    className="flex items-center space-x-2 px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700"
                >
                    <Plus className="h-4 w-4" />
                    <span>Ajouter</span>
                </button>
            </div>

            {showAddForm && (
                <div className="bg-white rounded-lg shadow p-6">
                    <form onSubmit={handleAddPassword} className="space-y-4">
                        <div>
                            <label className="block text-sm font-medium text-gray-700">Mot de passe</label>
                            <input
                                type="text"
                                value={newPassword.mot_de_passe}
                                onChange={(e) => setNewPassword({ ...newPassword, mot_de_passe: e.target.value })}
                                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                                required
                            />
                        </div>
                        <div>
                            <label className="block text-sm font-medium text-gray-700">Type</label>
                            <select
                                value={newPassword.info}
                                onChange={(e) => setNewPassword({ ...newPassword, info: e.target.value })}
                                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                            >
                                <option value="Operateur">Opérateur</option>
                                <option value="Admin">Admin</option>
                            </select>
                        </div>
                        <div className="flex space-x-2">
                            <button
                                type="submit"
                                disabled={loading}
                                className="px-4 py-2 bg-green-600 text-white rounded-md hover:bg-green-700 disabled:opacity-50"
                            >
                                {loading ? 'Ajout...' : 'Ajouter'}
                            </button>
                            <button
                                type="button"
                                onClick={() => setShowAddForm(false)}
                                className="px-4 py-2 bg-gray-600 text-white rounded-md hover:bg-gray-700"
                            >
                                Annuler
                            </button>
                        </div>
                    </form>
                </div>
            )}

            <div className="bg-white rounded-lg shadow overflow-hidden">
                <table className="min-w-full divide-y divide-gray-200">
                    <thead className="bg-gray-50">
                        <tr>
                            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                ID
                            </th>
                            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                Mot de passe
                            </th>
                            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                Type
                            </th>
                            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                Date création
                            </th>
                            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                Actions
                            </th>
                        </tr>
                    </thead>
                    <tbody className="bg-white divide-y divide-gray-200">
                        {passwords.map((password) => (
                            <tr key={password.id_motdepasse}>
                                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                                    {password.id_motdepasse}
                                </td>
                                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                                    <div className="flex items-center space-x-2">
                                        <span className="font-mono">
                                            {visiblePasswords[password.id_motdepasse]
                                                ? password.mot_de_passe
                                                : '••••••••'
                                            }
                                        </span>
                                        <button
                                            onClick={() => togglePasswordVisibility(password.id_motdepasse)}
                                            className="text-gray-400 hover:text-gray-600"
                                        >
                                            {visiblePasswords[password.id_motdepasse] ?
                                                <EyeOff className="h-4 w-4" /> :
                                                <Eye className="h-4 w-4" />
                                            }
                                        </button>
                                    </div>
                                </td>
                                <td className="px-6 py-4 whitespace-nowrap">
                                    <span className={`px-2 py-1 text-xs font-semibold rounded-full ${password.info === 'Admin'
                                            ? 'bg-red-100 text-red-800'
                                            : 'bg-blue-100 text-blue-800'
                                        }`}>
                                        {password.info}
                                    </span>
                                </td>
                                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                                    {new Date(password.date_creation).toLocaleDateString()}
                                </td>
                                <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                                    <button
                                        onClick={() => handleDeletePassword(password.id_motdepasse)}
                                        className="text-red-600 hover:text-red-900"
                                    >
                                        <Trash2 className="h-4 w-4" />
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
};

