import { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import '../css/carte.css';
import React from "react";
import Tesseract from "tesseract.js";

// Fix Leaflet marker icon issue
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png',
  iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
});

const Carte = () => {
  const [lieux, setLieux] = useState([]);
  const [rapportTitres, setRapportTitres] = useState({});
  const mapRef = useRef(null);
  const mapContainerRef = useRef(null);
  const [pointActuel, setPointActuel] = useState(null);
  const markerRefs = useRef([]);
  const gifOverlayRef = useRef(null);
  const [pendingGif, setPendingGif] = useState(null);

  // Ajout pour coordonn√©es extraites par Tesseract
  const [parsedCoords, setParsedCoords] = useState({ hautGauche: { lat: '', lng: '' }, basDroite: { lat: '', lng: '' } });
  const [isParsing, setIsParsing] = useState(false);

  const API_BASE_URL = process.env.REACT_APP_API_URL;

  // Fonction pour convertir DMS en d√©cimal avec support de formats vari√©s
  function dmsToDecimal(degreesStr, minutesStr, direction) {
    // Nettoyer les cha√Ænes en supprimant les caract√®res parasites
    const degrees = parseInt(degreesStr.replace(/[^\d]/g, ''), 10);
    const minutes = parseFloat(minutesStr.replace(',', '.').replace(/[^\d.]/g, ''));
    
    let decimal = degrees + (minutes / 60);
    
    if (direction === 'S' || direction === 'W') {
      decimal *= -1;
    }
    
    return decimal;
  }

  // Fonction am√©lior√©e pour parser les coordonn√©es du quadrillage
  const parseGifForGridCoords = async (gifDataUrl) => {
    setIsParsing(true);
    return new Promise((resolve) => {
      const img = document.createElement("img");
      img.src = gifDataUrl;
      img.onload = async () => {
        try {
          // Cr√©er un canvas pour traiter l'image
          const canvas = document.createElement("canvas");
          canvas.width = img.width;
          canvas.height = img.height;
          const ctx = canvas.getContext("2d");
          ctx.drawImage(img, 0, 0);
          
          // Pr√©processing pour am√©liorer la reconnaissance
          const imageData = ctx.getImageData(0, 0, canvas.width, canvas.height);
          const data = imageData.data;
          
          // Augmenter le contraste pour mieux distinguer le texte du quadrillage
          for (let i = 0; i < data.length; i += 4) {
            const avg = (data[i] + data[i + 1] + data[i + 2]) / 3;
            // Am√©liorer le contraste pour le texte noir sur fond clair
            const contrast = avg < 180 ? 0 : 255;
            data[i] = data[i + 1] = data[i + 2] = contrast;
          }
          
          ctx.putImageData(imageData, 0, 0);
          const processedDataUrl = canvas.toDataURL("image/png");

          // OCR avec Tesseract avec param√®tres optimis√©s pour les cartes
          const { data: { text } } = await Tesseract.recognize(processedDataUrl, "eng", {
            logger: m => console.log(m),
            tessedit_char_whitelist: '0123456789¬∞\'\"NSEW-.,',
            psm: 6, // Uniform block of text
          });

          console.log("Texte extrait:", text);

          // Patterns pour diff√©rents formats de coordonn√©es trouv√©s sur les cartes m√©t√©o
          const patterns = [
            // Format principal: 49¬∞54'N, 1¬∞36'W (axes de la carte)
            /(\d+)¬∞(\d+)'([NS])/g,
            /(\d+)¬∞(\d+)'([EW])/g,
            // Format alternatif: 49¬∞54N, 1¬∞36W
            /(\d+)¬∞(\d+)([NS])/g,
            /(\d+)¬∞(\d+)([EW])/g,
            // Format avec minutes d√©cimales: 49¬∞54.0'N
            /(\d+)¬∞(\d+\.?\d*)'?([NS])/g,
            /(\d+)¬∞(\d+\.?\d*)'?([EW])/g,
          ];

          let latitudes = [];
          let longitudes = [];

          // Extraire toutes les coordonn√©es avec tous les patterns
          patterns.forEach(pattern => {
            let match;
            while ((match = pattern.exec(text)) !== null) {
              const degrees = match[1];
              const minutes = match[2] || '0';
              const direction = match[3];
              
              const decimal = dmsToDecimal(degrees, minutes, direction);
              
              if (direction === 'N' || direction === 'S') {
                if (decimal >= -90 && decimal <= 90) { // Validation latitude
                  latitudes.push(decimal);
                }
              } else if (direction === 'E' || direction === 'W') {
                if (decimal >= -180 && decimal <= 180) { // Validation longitude
                  longitudes.push(decimal);
                }
              }
            }
          });

          // Supprimer les doublons et trier
          latitudes = [...new Set(latitudes)].sort((a, b) => b - a); // Du plus grand au plus petit
          longitudes = [...new Set(longitudes)].sort((a, b) => a - b); // Du plus petit au plus grand

          console.log("Latitudes trouv√©es:", latitudes);
          console.log("Longitudes trouv√©es:", longitudes);

          // D√©terminer les coins de la carte
          let hautGauche = { lat: "", lng: "" };
          let basDroite = { lat: "", lng: "" };

          if (latitudes.length >= 2 && longitudes.length >= 2) {
            // Prendre les extr√™mes pour d√©finir le rectangle englobant
            const latMax = Math.max(...latitudes);
            const latMin = Math.min(...latitudes);
            const lngMin = Math.min(...longitudes);
            const lngMax = Math.max(...longitudes);

            hautGauche = { lat: latMax, lng: lngMin };
            basDroite = { lat: latMin, lng: lngMax };
          } else {
            console.warn("Pas assez de coordonn√©es trouv√©es pour d√©finir les coins");
          }

          setIsParsing(false);
          resolve({ hautGauche, basDroite });

        } catch (error) {
          console.error("Erreur lors du parsing:", error);
          setIsParsing(false);
          resolve({ hautGauche: { lat: "", lng: "" }, basDroite: { lat: "", lng: "" } });
        }
      };
      
      img.onerror = () => {
        console.error("Erreur de chargement de l'image");
        setIsParsing(false);
        resolve({ hautGauche: { lat: "", lng: "" }, basDroite: { lat: "", lng: "" } });
      };
    });
  };

  // G√®re la s√©lection d'un fichier GIF par l'utilisateur
  const handleGifUpload = async (event) => {
    const file = event.target.files[0];
    if (!file || !file.name.endsWith('.gif')) return;

    const reader = new FileReader();
    reader.onload = async function (e) {
      setPendingGif(e.target.result);
      console.log("D√©but de l'analyse du GIF...");
      // Appel du parser pour extraire les coordonn√©es du quadrillage
      const coords = await parseGifForGridCoords(e.target.result);
      console.log("Coordonn√©es extraites:", coords);
      setParsedCoords(coords);
    };
    reader.readAsDataURL(file);
  };

  // Valide et superpose le GIF sur la carte Leaflet en utilisant les coordonn√©es du quadrillage
  const handleValidateGif = () => {
    if (!pendingGif || !mapRef.current) return;

    const img = new window.Image();
    img.crossOrigin = 'anonymous';
    img.onload = () => {
      // Utiliser les coordonn√©es extraites du quadrillage
      const hautGauche = parsedCoords.hautGauche;
      const basDroite = parsedCoords.basDroite;

      // V√©rifier que les coordonn√©es sont valides
      if (!hautGauche.lat || !hautGauche.lng || !basDroite.lat || !basDroite.lng) {
        console.warn("Coordonn√©es du quadrillage non disponibles, utilisation des valeurs par d√©faut");
        // Valeurs par d√©faut pour la zone Bretagne/Manche
        const bounds = [
          [48.5, -5.0], // Sud-Ouest
          [50.5, -1.0]  // Nord-Est
        ];
        
        if (gifOverlayRef.current) {
          mapRef.current.removeLayer(gifOverlayRef.current);
          gifOverlayRef.current = null;
        }

        const overlay = L.imageOverlay(pendingGif, bounds, {
          opacity: 0.7,
          interactive: false
        });
        overlay.addTo(mapRef.current);
        gifOverlayRef.current = overlay;
      } else {
        // Utiliser les coordonn√©es extraites du quadrillage
        const bounds = [
          [parseFloat(basDroite.lat), parseFloat(hautGauche.lng)], // Sud-Ouest
          [parseFloat(hautGauche.lat), parseFloat(basDroite.lng)]  // Nord-Est
        ];

        console.log("Bounds calcul√©s:", bounds);

        if (gifOverlayRef.current) {
          mapRef.current.removeLayer(gifOverlayRef.current);
          gifOverlayRef.current = null;
        }

        const overlay = L.imageOverlay(pendingGif, bounds, {
          opacity: 0.7,
          interactive: false
        });
        overlay.addTo(mapRef.current);
        gifOverlayRef.current = overlay;

        // Ajuster la vue de la carte sur la zone du GIF
        mapRef.current.fitBounds(bounds);
      }

      setPendingGif(null);
      console.log("GIF superpos√© avec succ√®s");
    };

    img.onerror = () => {
      console.error('Erreur de chargement de l\'image');
      setPendingGif(null);
    };

    img.src = pendingGif;
  };

  // Fonction pour r√©cup√©rer les titres des rapports
  const fetchRapportTitres = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/rapports`);
      // Cr√©er un objet avec les id_rapport comme cl√©s et les titres comme valeurs
      const titres = {};
      response.data.forEach(rapport => {
        titres[rapport.id_rapport] = rapport.titre || 'Rapport sans titre';
      });
      setRapportTitres(titres);
      console.log('Titres des rapports r√©cup√©r√©s:', titres);
    } catch (error) {
      console.error('Erreur lors de la r√©cup√©ration des titres de rapports:', error);
    }
  };

  // Ajoute cette fonction avant le useEffect principal
  const fetchLieux = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/lieux`);
      setLieux(response.data);
    } catch (error) {
      console.error('Erreur lors de la r√©cup√©ration des lieux:', error);
    }
  };

  // Initialiser la carte et charger les donn√©es
  useEffect(() => {
    // Initialiser la carte uniquement si le container existe et que la carte n'est pas encore initialis√©e
    if (!mapRef.current && mapContainerRef.current) {
      // Utilisation de la couche SHOM Raster Littoral (WMTS)
      // Documentation : https://data.shom.fr
      // URL WMTS : https://wxs.ign.fr/essentiels/geoportail/wmts?SERVICE=WMTS&REQUEST=GetCapabilities
      // Pour la d√©mo, on utilise le proxy du G√©oportail (IGN) qui propose la couche SHOM
      const shomLayer = L.tileLayer(
        'https://wxs.ign.fr/essentiels/geoportail/wmts?layer=GEOGRAPHICALGRIDSYSTEMS.MAPS.SCANLITTORALE&style=normal&tilematrixset=PM&Service=WMTS&Request=GetTile&Version=1.0.0&Format=image/jpeg&TileMatrix={z}&TileCol={x}&TileRow={y}',
        {
          attribution: '&copy; <a href="https://www.shom.fr/">SHOM</a> / <a href="https://www.ign.fr/">IGN</a>',
          maxZoom: 18,
          tileSize: 256,
        }
      );

      // Couche OpenStreetMap en attendant une cl√© SHOM/IGN valide
      const baseLayer = L.tileLayer(
        'https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png',
        {
          attribution: '&copy; OpenStreetMap contributors',
          maxZoom: 18,
        }
      );

      mapRef.current = L.map(mapContainerRef.current).setView([48.2, -3.5], 8); // Bretagne
      baseLayer.addTo(mapRef.current);

      // Contr√¥les de navigation maritime (optionnel)
      const couchesNavigation = {
        "Carte OpenStreetMap": baseLayer
      };

      L.control.layers(couchesNavigation, {}, {
        position: 'topright',
        collapsed: false
      }).addTo(mapRef.current);

      // Ajouter une √©chelle
      L.control.scale({
        metric: true,
        imperial: false,
        position: 'bottomleft'
      }).addTo(mapRef.current);
    }

    // Effectuer la r√©cup√©ration des donn√©es au montage du composant
    fetchLieux();
    fetchRapportTitres();

    return () => {
      // Cleanup de la carte lorsque le composant est d√©mont√©
      if (mapRef.current) {
        mapRef.current.remove();
        mapRef.current = null;
      }
    };
  }, [API_BASE_URL]);

  // Ajouter les marqueurs une fois que les lieux et les titres des rapports sont disponibles
  useEffect(() => {
    if (mapRef.current && lieux.length > 0) {
      // Supprimer uniquement les anciens marqueurs
      markerRefs.current.forEach(marker => {
        mapRef.current.removeLayer(marker);
      });
      markerRefs.current = [];

      // Cr√©er un dictionnaire pour regrouper les lieux par coordonn√©es
      const coordsMap = {};

      lieux.forEach(lieu => {
        const coordKey = `${lieu.latitude},${lieu.longitude}`;
        if (!coordsMap[coordKey]) {
          coordsMap[coordKey] = [];
        }
        coordsMap[coordKey].push(lieu);
      });

      // Ajouter un marqueur pour chaque coordonn√©e unique
      Object.entries(coordsMap).forEach(([coords, lieuxAtCoord]) => {
        const [lat, lng] = coords.split(',');

        // Ne pas ajouter de marqueur si les coordonn√©es sont invalides
        if (isNaN(parseFloat(lat)) || isNaN(parseFloat(lng))) {
          console.warn('Coordonn√©es invalides:', coords);
          return;
        }

        // Utiliser une ic√¥ne de navigation maritime conforme √† la signalisation
        const iconeNavigation = L.divIcon({
          className: 'marqueur-navigation-maritime',
          html: `<div style="
            background: linear-gradient(45deg, #ffffff, #e3f2fd);
            border: 3px solid #1565c0;
            border-radius: 50%;
            width: 26px;
            height: 26px;
            display: flex;
            align-items: center;
            justify-content: center;
            box-shadow: 0 3px 8px rgba(21,101,192,0.4);
            font-size: 14px;
            font-weight: bold;
            color: #1565c0;
          ">‚öì</div>`,
          iconSize: [30, 30],
          iconAnchor: [15, 15]
        });

        const marker = L.marker([parseFloat(lat), parseFloat(lng)], { icon: iconeNavigation }).addTo(mapRef.current);

        // Cr√©er le contenu du popup avec informations de navigation maritime
        let popupContent = `
          <div>
            <strong>POSITION DE NAVIGATION</strong>
            <div>
              <strong>Coordonn√©es :</strong>
              <ul>
                <li>Lat: ${parseFloat(lat).toFixed(4)}¬∞</li>
                <li>Lon: ${parseFloat(lng).toFixed(4)}¬∞</li>
              </ul>
            </div>
            <div>
              <strong>Points d'int√©r√™t maritime :</strong>
              <ul>
                ${lieuxAtCoord.map(lieu => `
                  <li>
                    ${lieu.details_lieu || 'Position non d√©finie'}<br/>
                    Rapport: ${rapportTitres[lieu.id_rapport] || `R√©f. ${lieu.id_rapport}`}<br/>
                    ID: ${lieu.id_lieu}<br/>
                    <button onclick="window.location.href='/rapport/${lieu.id_rapport}'" style="margin-top:4px;padding:2px 8px;font-size:13px;cursor:pointer;">
                      Visualiser le rapport
                    </button>
                  </li>
                `).join('')}
              </ul>
            </div>
          </div>
        `;

        marker.bindPopup(popupContent);

        // Ouvrir le popup si c'est le point actuel
        if (pointActuel && lieuxAtCoord.some(lieu => lieu.id_lieu === pointActuel.id_lieu)) {
          marker.openPopup();
        }

        markerRefs.current.push(marker); // Stocker le marqueur pour le nettoyage futur
      });
    }
  }, [lieux, rapportTitres, pointActuel]);

  return (
    <div className="page-carte">
      <div className="carte-header">
        <h2>Carte Marine </h2>
      </div>
      <input
        type="file"
        accept="image/gif"
        onChange={handleGifUpload}
        style={{ margin: '1em 0' }}
      />

      {/* Affichage du chargement */}
      {isParsing && (
        <div style={{ marginBottom: '1em', color: '#1565c0', fontWeight: 'bold' }}>
          üîç Analyse du quadrillage de la carte en cours...
        </div>
      )}

      {/* Affichage des coordonn√©es extraites du quadrillage */}
      <div style={{ marginBottom: '1em', minHeight: 24 }}>
        <label>
          Coin Nord-Ouest (Haut gauche)&nbsp;
          <input
            type="text"
            value={parsedCoords.hautGauche ? `${parsedCoords.hautGauche.lat}¬∞, ${parsedCoords.hautGauche.lng}¬∞` : ""}
            readOnly
            style={{ width: 200, marginRight: 10, background: "#e8f5e8", fontFamily: "monospace" }}
            placeholder="Lat¬∞, Lng¬∞"
          />
        </label>
        <label>
          Coin Sud-Est (Bas droite)&nbsp;
          <input
            type="text"
            value={parsedCoords.basDroite ? `${parsedCoords.basDroite.lat}¬∞, ${parsedCoords.basDroite.lng}¬∞` : ""}
            readOnly
            style={{ width: 200, background: "#e8f5e8", fontFamily: "monospace" }}
            placeholder="Lat¬∞, Lng¬∞"
          />
        </label>
      </div>

      {pendingGif && (
        <div style={{ margin: '1em 0', padding: '10px', background: '#f0f8ff', border: '1px solid #1565c0', borderRadius: '4px' }}>
          <span>üìç GIF analys√© et pr√™t √† √™tre superpos√© sur la carte selon son quadrillage g√©ographique.</span>
          <button 
            onClick={handleValidateGif} 
            style={{ 
              marginLeft: 10, 
              padding: '5px 15px', 
              background: '#1565c0', 
              color: 'white', 
              border: 'none', 
              borderRadius: '4px',
              cursor: 'pointer'
            }}
          >
            üìå Superposer sur la carte
          </button>
        </div>
      )}
      <div
        ref={mapContainerRef}
        className="map-container"
      />
    </div>
  );
};

export default Carte;
