import React, { useEffect, useState } from "react";
import axios from "axios";

const Password = () => {
  const [adminPassword, setAdminPassword] = useState("");
  const [userPassword, setUserPassword] = useState("");
  const [visible, setVisible] = useState({ admin: false, user: false });
  const [editMode, setEditMode] = useState({ admin: false, user: false });
  const [editValue, setEditValue] = useState({ admin: "", user: "" });
  const [popupOpen, setPopupOpen] = useState({ admin: false, user: false });
  const [popupConfirm, setPopupConfirm] = useState({ admin: "", user: "" });
  const [popupError, setPopupError] = useState({ admin: "", user: "" });
  const [error, setError] = useState({ admin: "", user: "" });

  const API_BASE_URL = process.env.REACT_APP_API_URL;

  useEffect(() => {
    const fetchPasswords = async () => {
      try {
        const res = await axios.get(`${API_BASE_URL}/admin/motdepasse`);
        const admin = res.data.find((p) => p.info === "Admin");
        const user = res.data.find((p) => p.info === "Opérateur");

        if (admin) setAdminPassword(admin.mot_de_passe);
        if (user) setUserPassword(user.mot_de_passe);
      } catch (err) {
        console.error("Erreur lors de la récupération des mots de passe", err);
      }
    };

    fetchPasswords();
  }, [API_BASE_URL]);

  const handleEdit = (type) => {
    setEditMode((prev) => ({ ...prev, [type]: true }));
    setEditValue((prev) => ({
      ...prev,
      [type]: type === "admin" ? adminPassword : userPassword,
    }));
  };

  const handleChange = (type, value) => {
    setEditValue((prev) => ({ ...prev, [type]: value }));
  };

  const handleSave = (type) => {
    setPopupOpen((prev) => ({ ...prev, [type]: true }));
    setPopupConfirm((prev) => ({ ...prev, [type]: "" }));
    setPopupError((prev) => ({ ...prev, [type]: "" }));
  };

  const handlePopupConfirm = async (type) => {
    if (popupConfirm[type] !== editValue[type]) {
      setPopupError((prev) => ({
        ...prev,
        [type]: "Les mots de passe ne correspondent pas.",
      }));
      return;
    }
    const id = type === "admin" ? 1 : 2;
    try {
      await axios.put(`${API_BASE_URL}/admin/motdepasse/${id}`, {
        mot_de_passe: editValue[type],
      });

      if (type === "admin") setAdminPassword(editValue.admin);
      if (type === "user") setUserPassword(editValue.user);

      setEditMode((prev) => ({ ...prev, [type]: false }));
      setPopupOpen((prev) => ({ ...prev, [type]: false }));
      setPopupConfirm((prev) => ({ ...prev, [type]: "" }));
      setPopupError((prev) => ({ ...prev, [type]: "" }));
      setError((prev) => ({ ...prev, [type]: "" }));
    } catch (err) {
      setPopupError((prev) => ({
        ...prev,
        [type]: "Erreur lors de la mise à jour",
      }));
      console.error("Erreur lors de la mise à jour", err);
    }
  };

  const renderPopup = (type) =>
    popupOpen[type] && (
      <div className="popup-overlay">
        <div className="popup-content">
          <h3>Confirmer le mot de passe</h3>
          <input
            type="password"
            placeholder="Retapez le mot de passe"
            value={popupConfirm[type]}
            onChange={(e) =>
              setPopupConfirm((prev) => ({ ...prev, [type]: e.target.value }))
            }
          />
          <div style={{ marginTop: 10 }}>
            <button
              className="password-btn password-btn-save"
              onClick={() => handlePopupConfirm(type)}
            >
              Confirmer
            </button>
            <button
              className="password-btn password-btn-cancel"
              onClick={() => {
                setPopupOpen((prev) => ({ ...prev, [type]: false }));
                setPopupError((prev) => ({ ...prev, [type]: "" }));
              }}
            >
              Annuler
            </button>
          </div>
          {popupError[type] && (
            <div className="password-error" style={{ color: "red" }}>
              {popupError[type]}
            </div>
          )}
        </div>
      </div>
    );

  const renderField = (label, type, password) => (
    <div className="password-block">
      <h2>{label}</h2>
      {editMode[type] ? (
        <div className="password-edit-row">
          <input
            className="password-input"
            type="password"
            placeholder="Nouveau mot de passe"
            value={editValue[type]}
            onChange={(e) => handleChange(type, e.target.value)}
          />
          <button
            className="password-btn password-btn-save"
            onClick={() => handleSave(type)}
          >
            Enregistrer
          </button>
          <button
            className="password-btn password-btn-cancel"
            onClick={() => {
              setEditMode((p) => ({ ...p, [type]: false }));
              setError((prev) => ({ ...prev, [type]: "" }));
            }}
          >
            Annuler
          </button>
          {error[type] && (
            <div className="password-error" style={{ color: "red" }}>
              {error[type]}
            </div>
          )}
          {renderPopup(type)}
        </div>
      ) : (
        <div className="password-row">
          <span className="password-value">
            {visible[type] ? password : "••••••••"}
          </span>
          <button
            className="password-btn password-btn-toggle"
            onClick={() =>
              setVisible((prev) => ({ ...prev, [type]: !prev[type] }))
            }
          >
            {visible[type] ? "Masquer" : "Afficher"}
          </button>
          <button
            className="password-btn password-btn-edit"
            onClick={() => handleEdit(type)}
          >
            Éditer
          </button>
        </div>
      )}
    </div>
  );

  return (
    <div className="passwords-container">
      {renderField("Mot de passe Admin", "admin", adminPassword)}
      {renderField("Mot de passe Utilisateur", "user", userPassword)}
    </div>
  );
};

export default Password;
