import { useEffect, useState } from 'react';
import axios from 'axios';

const API = process.env.REACT_APP_API_URL;

const AdminDashboard = () => {
  const [typesCible, setTypesCible] = useState([]);
  const [newType, setNewType] = useState('');
  const [editType, setEditType] = useState(null);
  const [editValue, setEditValue] = useState('');

  // Charger les types de cible
  useEffect(() => {
    fetchTypes();
  }, []);

  const fetchTypes = async () => {
    try {
      const res = await axios.get(`${API}/rapports/type-cible`);
      setTypesCible(res.data);
    } catch (err) {
      console.error('Erreur chargement types cible', err);
    }
  };

  // Ajouter un type
  const handleAdd = async (e) => {
    e.preventDefault();
    if (!newType.trim()) return;
    try {
      await axios.post(`${API}/rapports/type-cible`, { libelle: newType });
      setNewType('');
      fetchTypes();
    } catch (err) {
      console.error('Erreur ajout', err);
    }
  };

  // Supprimer un type
  const handleDelete = async (id) => {
    if (!window.confirm('Supprimer ce type ?')) return;
    try {
      await axios.delete(`${API}/rapports/type-cible/${id}`);
      fetchTypes();
    } catch (err) {
      console.error('Erreur suppression', err);
    }
  };

  // Modifier un type
  const handleEdit = (type) => {
    setEditType(type.id_type_cible);
    setEditValue(type.libelle);
  };

  const handleEditSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`${API}/rapports/type-cible/${editType}`, { libelle: editValue });
      setEditType(null);
      setEditValue('');
      fetchTypes();
    } catch (err) {
      console.error('Erreur modification', err);
    }
  };

  return (
    <div style={{ maxWidth: 500, margin: 'auto' }}>
      <h2>Administration - Types de cible</h2>
      <form onSubmit={handleAdd} style={{ marginBottom: 20 }}>
        <input
          type="text"
          value={newType}
          onChange={e => setNewType(e.target.value)}
          placeholder="Nouveau type de cible"
        />
        <button type="submit">Ajouter</button>
      </form>
      <ul>
        {typesCible.map(type =>
          <li key={type.id_type_cible} style={{ marginBottom: 8 }}>
            {editType === type.id_type_cible ? (
              <form onSubmit={handleEditSubmit} style={{ display: 'inline' }}>
                <input
                  type="text"
                  value={editValue}
                  onChange={e => setEditValue(e.target.value)}
                  autoFocus
                />
                <button type="submit">Enregistrer</button>
                <button type="button" onClick={() => setEditType(null)}>Annuler</button>
              </form>
            ) : (
              <>
                {type.libelle}
                <button onClick={() => handleEdit(type)} style={{ marginLeft: 10 }}>Modifier</button>
                <button onClick={() => handleDelete(type.id_type_cible)} style={{ marginLeft: 5, color: 'red' }}>Supprimer</button>
              </>
            )}
          </li>
        )}
      </ul>
    </div>
  );
};

export default AdminDashboard;
