import { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import '../css/carte.css';
import React from "react";
import Tesseract from "tesseract.js";

// Fix Leaflet marker icon issue
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png',
  iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
});

const Carte = () => {
  const [lieux, setLieux] = useState([]);
  const [rapportTitres, setRapportTitres] = useState({});
  const mapRef = useRef(null);
  const mapContainerRef = useRef(null);
  const [pointActuel, setPointActuel] = useState(null);
  const markerRefs = useRef([]);
  const gifOverlayRef = useRef(null);
  const [pendingGif, setPendingGif] = useState(null);

  // Suppression de la saisie manuelle
  // const [manualCoords, setManualCoords] = useState({ lat: '', lng: '' });

  // Ajout pour coordonnées extraites par Tesseract
  const [parsedCoords, setParsedCoords] = useState({ lat: '', lng: '' });
  const [isParsing, setIsParsing] = useState(false); // Ajout de l'état de chargement

  const API_BASE_URL = process.env.REACT_APP_API_URL;

  // Fonction pour convertir "49° 35,98'" en décimal
  function dmToDecimal(degrees, minutes, sign = 1) {
    // Remplace la virgule par un point pour les décimales
    const min = parseFloat(minutes.replace(',', '.'));
    return sign * (parseInt(degrees, 10) + min / 60);
  }

  // Nouvelle fonction pour convertir "49°24'N" ou "0°36'W" en décimal
  function dmsToDecimal(degrees, minutes, direction) {
    const deg = parseInt(degrees, 10);
    const min = parseFloat(minutes.replace(',', '.'));
    let decimal = deg + min / 60;
    if (direction === 'S' || direction === 'W') decimal *= -1;
    return decimal;
  }

  // Fonction pour parser toutes les coordonnées du GIF via Tesseract
  const parseGifForGridCoords = async (gifDataUrl) => {
    setIsParsing(true);
    return new Promise((resolve) => {
      const img = document.createElement("img");
      img.src = gifDataUrl;
      img.onload = async () => {
        const canvas = document.createElement("canvas");
        canvas.width = img.width;
        canvas.height = img.height;
        const ctx = canvas.getContext("2d");
        ctx.drawImage(img, 0, 0);
        const dataUrl = canvas.toDataURL("image/png");
        const { data: { text } } = await Tesseract.recognize(dataUrl, "eng");

        // Regex pour extraire toutes les latitudes et longitudes au format DMS
        // Ex : 49°24'N ou 0°36'W
        const latRegex = /(\d{1,2})[°º]\s*(\d{1,2})['’′]?\s*([NS])/g;
        const lonRegex = /(\d{1,3})[°º]\s*(\d{1,2})['’′]?\s*([EW])/g;

        let latitudes = [];
        let longitudes = [];
        let match;

        while ((match = latRegex.exec(text)) !== null) {
          latitudes.push(dmsToDecimal(match[1], match[2], match[3]));
        }
        while ((match = lonRegex.exec(text)) !== null) {
          longitudes.push(dmsToDecimal(match[1], match[2], match[3]));
        }

        // Trouver les extrêmes
        const latMax = latitudes.length ? Math.max(...latitudes) : "";
        const latMin = latitudes.length ? Math.min(...latitudes) : "";
        const lonMax = longitudes.length ? Math.max(...longitudes) : "";
        const lonMin = longitudes.length ? Math.min(...longitudes) : "";

        setIsParsing(false);
        resolve({
          hautGauche: { lat: latMax, lng: lonMin },
          basDroite: { lat: latMin, lng: lonMax }
        });
      };
      img.onerror = () => {
        setIsParsing(false);
        resolve({ hautGauche: { lat: "", lng: "" }, basDroite: { lat: "", lng: "" } });
      };
    });
  };

  // Gère la sélection d'un fichier GIF par l'utilisateur
  const handleGifUpload = async (event) => {
    const file = event.target.files[0];
    if (!file || !file.name.endsWith('.gif')) return;

    const reader = new FileReader();
    reader.onload = async function (e) {
      setPendingGif(e.target.result);
      // Appel du nouveau parser
      const coords = await parseGifForGridCoords(e.target.result);
      setParsedCoords(coords);
    };
    reader.readAsDataURL(file);
  };

  // Valide et superpose le GIF sur la carte Leaflet
  // Variables pour ajuster la position et l'étirement du GIF
  const decalageX = 0.622; // Décalage horizontal (axe X)
  const decalageY = 0.060; // Décalage vertical (axe Y)
  const etirementX = 0.60; // Étirement horizontal (largeur)
  const etirementY = 0.66; // Étirement vertical (hauteur)

  const handleValidateGif = () => {
    if (!pendingGif || !mapRef.current) return;

    const img = new window.Image();
    img.crossOrigin = 'anonymous';
    img.onload = () => {
      const imageRatio = img.height / img.width;

      // Utiliser les coordonnées extraites par Tesseract
      let centerLat = parseFloat(parsedCoords.lat);
      let centerLng = parseFloat(parsedCoords.lng);

      // Si non valides, utiliser valeurs par défaut
      if (isNaN(centerLat) || isNaN(centerLng)) {
        centerLat = 49.87 + decalageY;
        centerLng = ((-2.18 + -0.7) / 2 + decalageX);
      }

      const widthDeg = 1.48 * etirementX;
      const heightDeg = widthDeg * imageRatio * etirementY;

      const south = centerLat - heightDeg / 2;
      const north = centerLat + heightDeg / 2;
      const west = centerLng - widthDeg / 2;
      const east = centerLng + widthDeg / 2;

      const bounds = [
        [south, west],
        [north, east]
      ];

      if (gifOverlayRef.current) {
        mapRef.current.removeLayer(gifOverlayRef.current);
        gifOverlayRef.current = null;
      }

      const overlay = L.imageOverlay(pendingGif, bounds, {
        opacity: 0.7,
        interactive: false
      });
      overlay.addTo(mapRef.current);
      gifOverlayRef.current = overlay;

      setPendingGif(null);
    };

    img.onerror = () => {
      console.error('Erreur de chargement de l\'image');
      setPendingGif(null);
    };

    img.src = pendingGif;
  };



  // Fonction pour récupérer les titres des rapports
  const fetchRapportTitres = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/rapports`);
      // Créer un objet avec les id_rapport comme clés et les titres comme valeurs
      const titres = {};
      response.data.forEach(rapport => {
        titres[rapport.id_rapport] = rapport.titre || 'Rapport sans titre';
      });
      setRapportTitres(titres);
      console.log('Titres des rapports récupérés:', titres);
    } catch (error) {
      console.error('Erreur lors de la récupération des titres de rapports:', error);
    }
  };

  // Ajoute cette fonction avant le useEffect principal
  const fetchLieux = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/lieux`);
      setLieux(response.data);
    } catch (error) {
      console.error('Erreur lors de la récupération des lieux:', error);
    }
  };

  // Initialiser la carte et charger les données
  useEffect(() => {
    // Initialiser la carte uniquement si le container existe et que la carte n'est pas encore initialisée
    if (!mapRef.current && mapContainerRef.current) {
      // Utilisation de la couche SHOM Raster Littoral (WMTS)
      // Documentation : https://data.shom.fr
      // URL WMTS : https://wxs.ign.fr/essentiels/geoportail/wmts?SERVICE=WMTS&REQUEST=GetCapabilities
      // Pour la démo, on utilise le proxy du Géoportail (IGN) qui propose la couche SHOM
      const shomLayer = L.tileLayer(
        'https://wxs.ign.fr/essentiels/geoportail/wmts?layer=GEOGRAPHICALGRIDSYSTEMS.MAPS.SCANLITTORALE&style=normal&tilematrixset=PM&Service=WMTS&Request=GetTile&Version=1.0.0&Format=image/jpeg&TileMatrix={z}&TileCol={x}&TileRow={y}',
        {
          attribution: '&copy; <a href="https://www.shom.fr/">SHOM</a> / <a href="https://www.ign.fr/">IGN</a>',
          maxZoom: 18,
          tileSize: 256,
        }
      );

      // Couche OpenStreetMap en attendant une clé SHOM/IGN valide
      const baseLayer = L.tileLayer(
        'https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png',
        {
          attribution: '&copy; OpenStreetMap contributors',
          maxZoom: 18,
        }
      );

      mapRef.current = L.map(mapContainerRef.current).setView([48.2, -3.5], 8); // Bretagne
      baseLayer.addTo(mapRef.current);

      // Contrôles de navigation maritime (optionnel)
      const couchesNavigation = {
        "Carte OpenStreetMap": baseLayer
      };

      L.control.layers(couchesNavigation, {}, {
        position: 'topright',
        collapsed: false
      }).addTo(mapRef.current);

      // Ajouter une échelle
      L.control.scale({
        metric: true,
        imperial: false,
        position: 'bottomleft'
      }).addTo(mapRef.current);
    }

    // Effectuer la récupération des données au montage du composant
    fetchLieux();
    fetchRapportTitres();

    return () => {
      // Cleanup de la carte lorsque le composant est démonté
      if (mapRef.current) {
        mapRef.current.remove();
        mapRef.current = null;
      }
    };
  }, [API_BASE_URL]);

  // Ajouter les marqueurs une fois que les lieux et les titres des rapports sont disponibles
  useEffect(() => {
    if (mapRef.current && lieux.length > 0) {
      // Supprimer uniquement les anciens marqueurs
      markerRefs.current.forEach(marker => {
        mapRef.current.removeLayer(marker);
      });
      markerRefs.current = [];

      // Créer un dictionnaire pour regrouper les lieux par coordonnées
      const coordsMap = {};

      lieux.forEach(lieu => {
        const coordKey = `${lieu.latitude},${lieu.longitude}`;
        if (!coordsMap[coordKey]) {
          coordsMap[coordKey] = [];
        }
        coordsMap[coordKey].push(lieu);
      });

      // Ajouter un marqueur pour chaque coordonnée unique
      Object.entries(coordsMap).forEach(([coords, lieuxAtCoord]) => {
        const [lat, lng] = coords.split(',');

        // Ne pas ajouter de marqueur si les coordonnées sont invalides
        if (isNaN(parseFloat(lat)) || isNaN(parseFloat(lng))) {
          console.warn('Coordonnées invalides:', coords);
          return;
        }

        // Utiliser une icône de navigation maritime conforme à la signalisation
        const iconeNavigation = L.divIcon({
          className: 'marqueur-navigation-maritime',
          html: `<div style="
            background: linear-gradient(45deg, #ffffff, #e3f2fd);
            border: 3px solid #1565c0;
            border-radius: 50%;
            width: 26px;
            height: 26px;
            display: flex;
            align-items: center;
            justify-content: center;
            box-shadow: 0 3px 8px rgba(21,101,192,0.4);
            font-size: 14px;
            font-weight: bold;
            color: #1565c0;
          ">⚓</div>`,
          iconSize: [30, 30],
          iconAnchor: [15, 15]
        });

        const marker = L.marker([parseFloat(lat), parseFloat(lng)], { icon: iconeNavigation }).addTo(mapRef.current);

        // Créer le contenu du popup avec informations de navigation maritime
        let popupContent = `
          <div>
            <strong>POSITION DE NAVIGATION</strong>
            <div>
              <strong>Coordonnées :</strong>
              <ul>
                <li>Lat: ${parseFloat(lat).toFixed(4)}°</li>
                <li>Lon: ${parseFloat(lng).toFixed(4)}°</li>
              </ul>
            </div>
            <div>
              <strong>Points d'intérêt maritime :</strong>
              <ul>
                ${lieuxAtCoord.map(lieu => `
                  <li>
                    ${lieu.details_lieu || 'Position non définie'}<br/>
                    Rapport: ${rapportTitres[lieu.id_rapport] || `Réf. ${lieu.id_rapport}`}<br/>
                    ID: ${lieu.id_lieu}<br/>
                    <button onclick="window.location.href='/rapport/${lieu.id_rapport}'" style="margin-top:4px;padding:2px 8px;font-size:13px;cursor:pointer;">
                      Visualiser le rapport
                    </button>
                  </li>
                `).join('')}
              </ul>
            </div>
          </div>
        `;

        marker.bindPopup(popupContent);

        // Ouvrir le popup si c'est le point actuel
        if (pointActuel && lieuxAtCoord.some(lieu => lieu.id_lieu === pointActuel.id_lieu)) {
          marker.openPopup();
        }

        markerRefs.current.push(marker); // Stocker le marqueur pour le nettoyage futur
      });
    }
  }, [lieux, rapportTitres, pointActuel]);

  return (
    <div className="page-carte">
      <div className="carte-header">
        <h2>Carte Marine </h2>
      </div>
      <input
        type="file"
        accept="image/gif"
        onChange={handleGifUpload}
        style={{ margin: '1em 0' }}
      />

      {/* Affichage du chargement */}
      {isParsing && (
        <div style={{ marginBottom: '1em', color: '#1565c0', fontWeight: 'bold' }}>
          Analyse du GIF en cours...
        </div>
      )}

      {/* Affichage des coordonnées extraites */}
      <div style={{ marginBottom: '1em', minHeight: 24 }}>
        <label>
          Haut gauche&nbsp;
          <input
            type="text"
            value={parsedCoords.hautGauche ? `${parsedCoords.hautGauche.lat}, ${parsedCoords.hautGauche.lng}` : ""}
            readOnly
            style={{ width: 180, marginRight: 10, background: "#e3f2fd" }}
            placeholder="Lat, Lng"
          />
        </label>
        <label>
          Bas droite&nbsp;
          <input
            type="text"
            value={parsedCoords.basDroite ? `${parsedCoords.basDroite.lat}, ${parsedCoords.basDroite.lng}` : ""}
            readOnly
            style={{ width: 180, background: "#e3f2fd" }}
            placeholder="Lat, Lng"
          />
        </label>
      </div>

      {pendingGif && (
        <div style={{ margin: '1em 0' }}>
          <span>GIF prêt à être superposé.</span>
          <button onClick={handleValidateGif} style={{ marginLeft: 10 }}>
            Valider
          </button>
        </div>
      )}
      <div
        ref={mapContainerRef}
        className="map-container"
      />
    </div>
  );
};

function CoordinateExtractor() {
  const [coords, setCoords] = useState({ lat: "", lon: "" });
  const [image, setImage] = useState(null);

  const handleGifUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    // Extraire la première frame du GIF
    const url = URL.createObjectURL(file);
    const img = document.createElement("img");
    img.src = url;
    img.onload = async () => {
      const canvas = document.createElement("canvas");
      canvas.width = img.width;
      canvas.height = img.height;
      const ctx = canvas.getContext("2d");
      ctx.drawImage(img, 0, 0);
      const dataUrl = canvas.toDataURL("image/png");

      // OCR avec Tesseract
      const { data: { text } } = await Tesseract.recognize(dataUrl, "eng");
      // Extraction simple des coordonnées (à adapter selon le format du texte)
      const latMatch = text.match(/Lat(?:itude)?[:\s]*([-\d.]+)/i);
      const lonMatch = text.match(/Lon(?:gitude)?[:\s]*([-\d.]+)/i);
      setCoords({
        lat: latMatch ? latMatch[1] : "Non trouvé",
        lon: lonMatch ? lonMatch[1] : "Non trouvé",
      });
    };
    setImage(url);
  };

  return (
    <div>
      <input type="file" accept="image/gif" onChange={handleGifUpload} />
      {image && <img src={image} alt="GIF preview" style={{ maxWidth: 300 }} />}
    </div>
  );
}

export default Carte;
