import { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import {
    Users,
    Key,
    BarChart3,
    File,
    Documents,
    LogOut,
    Plus,
    Trash2,
    Eye,
    Shield,
    UserCheck,
    Database,
    EyeOff,
    Edit,
    Save,
    X
} from 'lucide-react';

import '../css/AdminDashboard.css';

const AdminDashboard = () => {
    const { isAdmin, isAuthenticated, logout, operateurNom } = useAuth();
    const navigate = useNavigate();
    const [activeTab, setActiveTab] = useState('dashboard');
    const [stats, setStats] = useState({});
    const [passwords, setPasswords] = useState([]);
    const [operateurs, setOperateurs] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    const API = process.env.REACT_APP_API_URL;

    // Vérifications de sécurité
    useEffect(() => {
        if (!isAuthenticated) {
            navigate('/auth');
            return;
        }
        if (!isAdmin) {
            navigate('/');
            return;
        }
    }, [isAuthenticated, isAdmin, navigate]);

    // Charger les données initiales
    useEffect(() => {
        if (isAdmin && isAuthenticated) {
            loadStats();
            loadPasswords();
            loadOperateurs();
        }
    }, [isAdmin, isAuthenticated]);

    const loadStats = async () => {
        try {
            const response = await fetch(`${API}/auth/stats`);
            const data = await response.json();
            if (data.success) {
                setStats(data.data);
            }
        } catch (err) {
            console.error('Erreur lors du chargement des statistiques:', err);
        }
    };

    const loadPasswords = async () => {
        try {
            const response = await fetch(`${API}/auth/passwords`);
            const data = await response.json();
            if (data.success) {
                setPasswords(data.data);
            }
        } catch (err) {
            console.error('Erreur lors du chargement des mots de passe:', err);
        }
    };

    const loadOperateurs = async () => {
        try {
            const response = await fetch(`${API}/auth/operateurs`);
            const data = await response.json();
            if (data.success) {
                setOperateurs(data.data[0] || []);
            }
        } catch (err) {
            console.error('Erreur lors du chargement des opérateurs:', err);
        }
    };

    const handleLogout = () => {
        logout();
        navigate('/auth');
    };

    if (!isAuthenticated || !isAdmin) {
        return <div className="flex items-center justify-center min-h-screen">Chargement...</div>;
    }

    return (
        <div className="admin-dashboard-root">
            {/* Header */}


            <div className="admin-main">
                {/* Navigation */}
                <nav className="admin-nav">
                    <ul className="admin-nav-list">
                        {[
                            { id: 'dashboard', label: 'Tableau de bord', icon: BarChart3 },
                            { id: 'passwords', label: 'Mots de passe', icon: Key },
                            { id: 'operateurs', label: 'Opérateurs', icon: Users },
                            { id: 'documents', label: 'Documents', icon: File }
                        ].map(tab => {
                            const Icon = tab.icon;
                            return (
                                <li key={tab.id}>
                                    <button
                                        onClick={() => setActiveTab(tab.id)}
                                        className={`admin-nav-btn ${activeTab === tab.id
                                                ? 'admin-nav-btn-active'
                                                : ''
                                            }`}
                                    >
                                        <Icon className="admin-nav-icon" />
                                        <span>{tab.label}</span>
                                    </button>
                                </li>
                            );
                        })}

                        <li
                            onClick={handleLogout}
                            className="admin-logout-btn"
                        >
                            
                            <button><LogOut className="admin-logout-icon" />Déconnexion</button>
                        </li>
                    </ul>

                </nav>

                <header className="admin-header">
                    <div className="admin-header-container">
                        <div className="admin-header-content">
                            <div className="admin-header-left">
                                
                                <div>
                                    <h1 className="admin-title">Panneau d'Administration</h1>
                                    <p className="admin-welcome">Bienvenue, {operateurNom}</p>
                                </div>
                            </div>

                        </div>
                    </div>
                </header>
                {/* Contenu principal */}
                {activeTab === 'dashboard' && <DashboardTab stats={stats} />}
                {activeTab === 'passwords' && <PasswordsTab passwords={passwords} onReload={loadPasswords} />}
                {activeTab === 'operateurs' && <OperateursTab operateurs={operateurs} onReload={loadOperateurs} />}
                {activeTab === 'documents' && <DocumentsTab />}
            </div>


        </div>



    );
};

// Composant Tableau de bord
const DashboardTab = ({ stats }) => (
   <div></div>
);

// Composant Carte de Statistique
const StatCard = ({ title, value, icon: Icon, color }) => {
<div></div>
};

// Composant Gestion des Mots de Passe
const PasswordsTab = ({ passwords, onReload }) => {
<div></div>
};

// Composant Gestion des Opérateurs
const OperateursTab = ({ operateurs, onReload }) => {
    const fetchOperateurs = async () => {
        const API = process.env.REACT_APP_API_URL;
        try {
            const response = await fetch(`${API}/auth/operateurs`);
            const data = await response.json();
            if (data.success) {
                return data.data[0] || [];
            }
        } catch (err) {
            console.error('Erreur lors du chargement des opérateurs:', err);
        }
    }
    
    <div>

    </div>
};

// Composant Paramètres
const DocumentsTab = () => {
const fetchDocuments = async () => {
    const API = process.env.REACT_APP_API_URL;
    try {
        const response = await fetch(`${API}/auth/documents`);
        const data = await response.json();
        if (data.success) {
            return data.data;
        }
    } catch (err) {
        console.error('Erreur lors du chargement des documents:', err);
    }
}

    <div></div>
};

export default AdminDashboard;
