import React, { useEffect, useRef, useState } from "react";
import 'leaflet/dist/leaflet.css';

const DetailsRapport = ({
  rapportSelectionne,
  formatDate,
  getOperateurNom,
  getTypeEvenementLibelle,
  getSousTypeEvenementLibelle,
  getOrigineEvenementLibelle,
  getZoneNom,
  historique,
}) => {
  const mapRef = useRef(null);
  const leafletMapRef = useRef(null);
  
  // États pour stocker les données de référence
  const [typesEvenement, setTypesEvenement] = useState([]);
  const [sousTypesEvenement, setSousTypesEvenement] = useState([]);
  const [originesEvenement, setOriginesEvenement] = useState([]);
  const [zonesGeographiques, setZonesGeographiques] = useState([]);

  // Fonctions pour récupérer les données de référence
  useEffect(() => {
    const fetchReferenceData = async () => {
      try {
        const API = process.env.REACT_APP_API_URL;
        
        // Récupérer tous les types d'événements
        const [typesRes, sousTypesRes, originesRes, zonesRes] = await Promise.all([
          fetch(`${API}/api/types-evenement`),
          fetch(`${API}/api/sous-types-evenement`),
          fetch(`${API}/api/origines-evenement`),
          fetch(`${API}/api/zones-geographiques`)
        ]);

        if (typesRes.ok) setTypesEvenement(await typesRes.json());
        if (sousTypesRes.ok) setSousTypesEvenement(await sousTypesRes.json());
        if (originesRes.ok) setOriginesEvenement(await originesRes.json());
        if (zonesRes.ok) setZonesGeographiques(await zonesRes.json());
      } catch (error) {
        console.error('Erreur lors de la récupération des données de référence:', error);
      }
    };

    fetchReferenceData();
  }, []);

  // Fonctions utilitaires pour convertir les IDs en libellés
  const getTypeEvenementLibelleLocal = (id) => {
    if (!id) return "Non défini";
    const type = typesEvenement.find(t => t.id_type_evenement === id);
    return type ? type.libelle : "Non défini";
  };

  const getSousTypeEvenementLibelleLocal = (id) => {
    if (!id) return "Non défini";
    const sousType = sousTypesEvenement.find(st => st.id_sous_type_evenement === id);
    return sousType ? sousType.libelle : "Non défini";
  };

  const getOrigineEvenementLibelleLocal = (id) => {
    if (!id) return "Non défini";
    const origine = originesEvenement.find(o => o.id_origine_evenement === id);
    return origine ? origine.libelle : "Non défini";
  };

  const getZoneNomLocal = (id) => {
    if (!id) return "Non défini";
    const zone = zonesGeographiques.find(z => z.id_zone === id);
    return zone ? zone.nom_zone : "Non défini";
  };

  // Fonction pour charger Leaflet
  const loadLeaflet = () => {
    return new Promise((resolve) => {
      if (window.L) {
        resolve();
      } else {
        const script = document.createElement('script');
        script.src = "https://unpkg.com/leaflet/dist/leaflet.js";
        script.onload = resolve;
        document.body.appendChild(script);
      }
    });
  };

  useEffect(() => {
    let isMounted = true;
    async function initMap() {
      if (
        mapRef.current &&
        rapportSelectionne &&
        rapportSelectionne.latitude &&
        rapportSelectionne.longitude
      ) {
        await loadLeaflet();
        if (!isMounted) return;
        
        // Nettoyer la carte précédente si elle existe
        if (leafletMapRef.current) {
          leafletMapRef.current.remove();
        }
        
        const lat = parseFloat(rapportSelectionne.latitude);
        const lng = parseFloat(rapportSelectionne.longitude);
        
        // Petit délai pour s'assurer que le conteneur est visible
        setTimeout(() => {
          if (!isMounted || !mapRef.current) return;
          
          leafletMapRef.current = window.L.map(mapRef.current, { 
            zoomControl: false, 
            dragging: false, 
            scrollWheelZoom: false, 
            doubleClickZoom: false, 
            boxZoom: false, 
            keyboard: false, 
            tap: false, 
            touchZoom: false 
          }).setView([lat, lng], 10);
          
          window.L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
            attribution: '&copy; OpenStreetMap contributors',
            maxZoom: 18
          }).addTo(leafletMapRef.current);
          
          window.L.marker([lat, lng]).addTo(leafletMapRef.current);
          
          // Désactive toutes les interactions
          leafletMapRef.current.dragging.disable();
          leafletMapRef.current.touchZoom.disable();
          leafletMapRef.current.doubleClickZoom.disable();
          leafletMapRef.current.scrollWheelZoom.disable();
          leafletMapRef.current.boxZoom.disable();
          leafletMapRef.current.keyboard.disable();
          if (leafletMapRef.current.tap) leafletMapRef.current.tap.disable();
          
          // IMPORTANT : Force le redimensionnement de la carte
          setTimeout(() => {
            if (leafletMapRef.current && isMounted) {
              leafletMapRef.current.invalidateSize();
            }
          }, 100);
          
        }, 100);
      }
    }
    initMap();
    return () => {
      isMounted = false;
      if (leafletMapRef.current) {
        leafletMapRef.current.remove();
        leafletMapRef.current = null;
      }
    };
  }, [rapportSelectionne]);

  if (!rapportSelectionne) return null;

  console.log("=== DEBUG DetailsRapport ===");
  console.log("rapportSelectionne complet:", rapportSelectionne);
  console.log("Nom zone:", rapportSelectionne.nom_zone_lieu);
  console.log("Nom cible:", rapportSelectionne.nom_cible);
  console.log("Type cible:", rapportSelectionne.type_cible_libelle);
  console.log("Immatriculation:", rapportSelectionne.immatriculation);
  console.log("==========================");

  return (
    <div className="details-rapport">
      <div className="rapport-header">
        <h3>{rapportSelectionne.titre}</h3>
        <span className="rapport-id">ID: {rapportSelectionne.id_rapport}</span>
      </div>

      <div className="rapport-sections">
        {/* Informations principales */}
        <div className="rapport-section infos-principales">
          <h4>Informations principales</h4>
          <div className="info-grid">
            <div className="info-item">
              <span className="info-label">Date de l'événement:</span>
              <span className="info-value">{formatDate(rapportSelectionne.date_evenement)}</span>
            </div>
            <div className="info-item">
              <span className="info-label">Date de création:</span>
              <span className="info-value">{formatDate(rapportSelectionne.date_creation)}</span>
            </div>
            {rapportSelectionne.date_modification && (
              <div className="info-item">
                <span className="info-label">Dernière modification:</span>
                <span className="info-value">{formatDate(rapportSelectionne.date_modification)}</span>
              </div>
            )}
            <div className="info-item">
              <span className="info-label">Type:</span>
              <span className="info-value info-tag type">
                {getTypeEvenementLibelleLocal(rapportSelectionne.id_type_evenement)}
              </span>
            </div>
            <div className="info-item">
              <span className="info-label">Sous-type:</span>
              <span className="info-value info-tag sous-type">
                {getSousTypeEvenementLibelleLocal(rapportSelectionne.id_sous_type_evenement)}
              </span>
            </div>
            <div className="info-item">
              <span className="info-label">Origine:</span>
              <span className="info-value info-tag origine">
                {getOrigineEvenementLibelleLocal(rapportSelectionne.id_origine_evenement)}
              </span>
            </div>
            {rapportSelectionne.id_zone_lieu && (
              <div className="info-item">
                <span className="info-label">Zone géographique:</span>
                <span className="info-value info-tag zone">
                  {rapportSelectionne.nom_zone_lieu || getZoneNomLocal(rapportSelectionne.id_zone_lieu)}
                </span>
              </div>
            )}
          </div>
        </div>

        {/* Description globale */}
        {rapportSelectionne.description_globale && (
          <div className="rapport-section description">
            <h4>Description globale</h4>
            <div className="description-content">
              {rapportSelectionne.description_globale}
            </div>
          </div>
        )}

        {/* Cible de l'événement - utilise directement les champs du rapport */}
        <div className="rapport-section cible">
          <h4>Cible de l'événement</h4>
          <div className="info-grid">
            <div className="info-item">
              <span className="info-label">Type de cible:</span>
              <span className="info-value">{rapportSelectionne.type_cible_libelle || "Non définie"}</span>
            </div>
            <div className="info-item">
              <span className="info-label">Nom de la cible:</span>
              <span className="info-value">{rapportSelectionne.nom_cible || "Non définie"}</span>
            </div>
            <div className="info-item">
              <span className="info-label">Pavillon:</span>
              <span className="info-value">{rapportSelectionne.pavillon_cible || "Non définie"}</span>
            </div>
            <div className="info-item">
              <span className="info-label">Immatriculation:</span>
              <span className="info-value">{rapportSelectionne.immatriculation || "Non définie"}</span>
            </div>
            <div className="info-item">
              <span className="info-label">Type de produit:</span>
              <span className="info-value">{rapportSelectionne.TypeProduit || "Non définie"}</span>
            </div>
            <div className="info-item">
              <span className="info-label">Quantité de produit:</span>
              <span className="info-value">{rapportSelectionne.QuantiteProduit || "Non définie"}</span>
            </div>
            <div className="info-item">
              <span className="info-label">ID Type Cible:</span>
              <span className="info-value">{rapportSelectionne.id_type_cible || "Non définie"}</span>
            </div>
          </div>
        </div>

        {/* Localisation - avec nom de la zone */}
        <div className="rapport-section localisation">
          <h4>Localisation</h4>
          <div className="info-grid">
            <div className="info-item">
              <span className="info-label">Zone géographique:</span>
              <span className="info-value">{rapportSelectionne.nom_zone_lieu || getZoneNomLocal(rapportSelectionne.id_zone_lieu) || "Non définie"}</span>
            </div>
            <div className="info-item">
              <span className="info-label">Précision sur le lieu:</span>
              <span className="info-value">{rapportSelectionne.details_lieu || "Non définie"}</span>
            </div>
            <div className="info-item">
              <span className="info-label">Latitude:</span>
              <span className="info-value">{rapportSelectionne.latitude ?? "Non définie"}</span>
            </div>
            <div className="info-item">
              <span className="info-label">Longitude:</span>
              <span className="info-value">{rapportSelectionne.longitude ?? "Non définie"}</span>
            </div>
          </div>
          {rapportSelectionne.latitude && rapportSelectionne.longitude && (
            <div style={{ height: "200px", width: "100%", marginTop: "10px" }}>
              <div
                ref={mapRef}
                style={{ height: "100%", width: "100%", borderRadius: "8px", border: "1px solid #ccc" }}
                id={`map-details-rapport-${rapportSelectionne.id_rapport}`}
              />
            </div>
          )}
        </div>

        {/* Conditions météorologiques - utilise directement les champs du rapport */}
        <div className="rapport-section meteo">
          <h4>Conditions météorologiques</h4>
          <div className="info-grid">
            <div className="info-item">
              <span className="info-label">Direction du vent:</span>
              <span className="info-value">{rapportSelectionne.direction_vent || "Non définie"}</span>
            </div>
            <div className="info-item">
              <span className="info-label">Force du vent:</span>
              <span className="info-value">{rapportSelectionne.force_vent ?? "Non définie"}</span>
            </div>
            <div className="info-item">
              <span className="info-label">État de la mer:</span>
              <span className="info-value">{rapportSelectionne.etat_mer ?? "Non définie"}</span>
            </div>
            <div className="info-item">
              <span className="info-label">Nébulosité:</span>
              <span className="info-value">{rapportSelectionne.nebulosite ?? "Non définie"}</span>
            </div>
            <div className="info-item">
              <span className="info-label">Marée:</span>
              <span className="info-value">{rapportSelectionne.maree || "Non définie"}</span>
            </div>
          </div>
        </div>

        {/* Alertes et contacts - utilise directement les champs du rapport */}
        <div className="rapport-section alertes">
          <h4>Contacts et alertes</h4>
          <div className="info-grid">
            <div className="info-item">
              <span className="info-label">CEDRE alerté:</span>
              <span className="info-value">{rapportSelectionne.cedre ? "Oui" : "Non"}</span>
            </div>
            <div className="info-item">
              <span className="info-label">CROSS alerté:</span>
              <span className="info-value">{rapportSelectionne.cross_contact ? "Oui" : "Non"}</span>
            </div>
            <div className="info-item">
              <span className="info-label">Photo:</span>
              <span className="info-value">{rapportSelectionne.photo ? "Oui" : "Non"}</span>
            </div>
            <div className="info-item">
              <span className="info-label">POLREP:</span>
              <span className="info-value">{rapportSelectionne.polrep ? "Oui" : "Non"}</span>
            </div>
            <div className="info-item">
              <span className="info-label">Dérive MOTHY:</span>
              <span className="info-value">{rapportSelectionne.derive_mothym ? "Oui" : "Non"}</span>
            </div>
            <div className="info-item">
              <span className="info-label">POLMAR Terre:</span>
              <span className="info-value">{rapportSelectionne.pne ? "Oui" : "Non"}</span>
            </div>
            <div className="info-item">
              <span className="info-label">SMP:</span>
              <span className="info-value">{rapportSelectionne.smp ? "Oui" : "Non"}</span>
            </div>
            <div className="info-item">
              <span className="info-label">BSAA:</span>
              <span className="info-value">{rapportSelectionne.bsaa ? "Oui" : "Non"}</span>
            </div>
            <div className="info-item">
              <span className="info-label">Message POLREP:</span>
              <span className="info-value">{rapportSelectionne.message_polrep || "Non définie"}</span>
            </div>
            {rapportSelectionne.bsaa && (
              <div className="info-item">
                <span className="info-label">Délai d'appareillage:</span>
                <span className="info-value">{rapportSelectionne.delai_appareillage_bsaa || "Non définie"}</span>
              </div>
            )}
            <div className="info-item">
              <span className="info-label">Moyens à proximité:</span>
              <span className="info-value">{rapportSelectionne.moyen_proximite || "Non défini"}</span>
            </div>
            <div className="info-item">
              <span className="info-label">Moyens dépêchés sur zone:</span>
              <span className="info-value">{rapportSelectionne.moyen_depeche || "Non défini"}</span>
            </div>
            <div className="info-item">
              <span className="info-label">Moyens maritimes ou de l'État:</span>
              <span className="info-value">{rapportSelectionne.moyen_marine_etat || "Non défini"}</span>
            </div>
            <div className="info-item">
              <span className="info-label">Risque court terme:</span>
              <span className="info-value">{rapportSelectionne.risque_court_terme || "Non défini"}</span>
            </div>
            <div className="info-item">
              <span className="info-label">Risque moyen/long terme:</span>
              <span className="info-value">{rapportSelectionne.risque_moyen_long_terme || "Non défini"}</span>
            </div>
          </div>
        </div>

        {/* Responsable */}
        <div className="rapport-section responsable">
          <h4>Responsable</h4>
          <div className="responsable-info">
            <div className="avatar">
              {getOperateurNom(rapportSelectionne.id_operateur).substring(0, 1).toUpperCase()}
            </div>
            <div className="responsable-details">
              <div className="responsable-nom">{getOperateurNom(rapportSelectionne.id_operateur)}</div>
              <div className="responsable-date">Créé le {formatDate(rapportSelectionne.date_creation)}</div>
              {rapportSelectionne.id_operateur_modification && (
                <div className="responsable-modification">
                  Modifié par {getOperateurNom(rapportSelectionne.id_operateur_modification)} le {formatDate(rapportSelectionne.date_modification)}
                </div>
              )}
            </div>
          </div>
        </div>

        {/* Accès partagés */}
        {rapportSelectionne.operateurs_acces && rapportSelectionne.operateurs_acces.length > 0 && (
          <div className="rapport-section acces">
            <h4>Accès partagés</h4>
            <div className="acces-liste">
              {rapportSelectionne.operateurs_acces.map((opId) => (
                <div key={opId} className="acces-item">
                  <span className="acces-avatar">{getOperateurNom(opId).substring(0, 1).toUpperCase()}</span>
                  <span className="acces-nom">{getOperateurNom(opId)}</span>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Informations supplémentaires */}
        {rapportSelectionne.informations_supplementaires && (
          <div className="rapport-section infos-supp">
            <h4>Informations supplémentaires</h4>
            <div className="infos-supp-content">
              {rapportSelectionne.informations_supplementaires}
            </div>
          </div>
        )}

        {/* Historique des actions */}
        <div className="rapport-section historique">
          <h4>Historique des actions</h4>
          <div className="historique-list">
            {historique && historique.length > 0 ? (
              historique.map((action, idx) => (
                <div key={idx} className="historique-item">
                  <div className="historique-item-header">
                    <span>
                      {new Date(action.date_action).toLocaleDateString('fr-FR', {
                        day: '2-digit',
                        month: '2-digit',
                        year: 'numeric',
                        hour: '2-digit',
                        minute: '2-digit'
                      })}
                      {action.type_action && ` : ${action.type_action}`}
                    </span>
                    {/* Affichage du nom de l'opérateur si disponible */}
                    {action.id_operateur && (
                      <span className="historique-operateur">
                        {getOperateurNom(action.id_operateur)}
                      </span>
                    )}
                  </div>
                  {action.detail_action && (
                    <div className="historique-detail">
                      {action.detail_action}
                    </div>
                  )}
                </div>
              ))
            ) : (
              <div>Aucun historique disponible.</div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default DetailsRapport;
