import React, { useEffect, useState, useRef } from "react";
import axios from "axios";
import { motion, AnimatePresence } from "framer-motion";

const Documents = () => {
  const [documents, setDocuments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [uploading, setUploading] = useState(false);
  const [error, setError] = useState(null);
  const API_BASE_URL = process.env.REACT_APP_API_URL;
  const fileInputRef = useRef(null);

  useEffect(() => {
    fetchDocuments();
    // eslint-disable-next-line
  }, [API_BASE_URL]);

  const fetchDocuments = async () => {
    setLoading(true);
    try {
      const res = await axios.get(`${API_BASE_URL}/admin/documents`);
      setDocuments(res.data);
      setError(null);
    } catch (err) {
      setError("Erreur lors de la récupération des documents");
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Supprimer ce document ?")) return;
    try {
      await axios.delete(`${API_BASE_URL}/admin/documents/${id}`);
      setDocuments((docs) => docs.filter((doc) => doc.id !== id));
    } catch (err) {
      alert("Erreur lors de la suppression");
    }
  };

  const handleEdit = (id) => {
    alert("Fonction d'édition à implémenter pour l'id: " + id);
  };

  const handleDrop = async (e) => {
    e.preventDefault();
    const file = e.dataTransfer.files[0];
    if (!file) return;
    await uploadFile(file);
  };

  const handleFileChange = async (e) => {
    const file = e.target.files[0];
    if (!file) return;
    await uploadFile(file);
  };

  const uploadFile = async (file) => {
    setUploading(true);
    const formData = new FormData();
    formData.append("document", file);
    try {
      await axios.post(`${API_BASE_URL}/admin/documents`, formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      fetchDocuments();
    } catch (err) {
      alert("Erreur lors de l'upload");
    } finally {
      setUploading(false);
    }
  };

  return (
    <motion.div
      className="documents-container"
      initial={{ opacity: 0, y: 30 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.6, ease: "easeOut" }}
    >
      <motion.h1
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.2, duration: 0.5 }}
      >
        Gestion des documents
      </motion.h1>

      <motion.div
        style={{
          border: "2px dashed #aaa",
          borderRadius: 8,
          padding: 24,
          textAlign: "center",
          marginBottom: 24,
          background: "#fafafa",
          cursor: "pointer",
          position: "relative",
        }}
        initial={{ opacity: 0, scale: 0.95 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{ delay: 0.3, duration: 0.5 }}
        onDrop={handleDrop}
        onDragOver={(e) => e.preventDefault()}
        onClick={() => fileInputRef.current.click()}
      >
        <span style={{ fontSize: 32, marginBottom: 8, display: "inline-block" }}>📤</span>
        <div>
          {uploading ? "Chargement..." : "Déposez un fichier ici ou cliquez pour ajouter"}
        </div>
        <input
          type="file"
          ref={fileInputRef}
          style={{ display: "none" }}
          onChange={handleFileChange}
        />
      </motion.div>

      <main>
        <AnimatePresence mode="wait">
          {loading ? (
            <motion.div
              key="loading"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
            >
              <div style={{ textAlign: "center", padding: 20 }}>Chargement...</div>
            </motion.div>
          ) : error ? (
            <motion.div
              key="error"
              initial={{ opacity: 0, scale: 0.95 }}
              animate={{ opacity: 1, scale: 1 }}
              exit={{ opacity: 0, scale: 0.95 }}
              style={{ color: "red", textAlign: "center", padding: 20 }}
            >
              {error}
            </motion.div>
          ) : (
            <motion.div
              className="documents-list"
              key="documents"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: 20 }}
              transition={{ duration: 0.5 }}
            >
              <motion.h2
                initial={{ opacity: 0, x: 20 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ delay: 0.2, duration: 0.5 }}
              >
                Liste des documents
              </motion.h2>
              {documents.length === 0 ? (
                <motion.p
                  className="no-documents"
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  transition={{ delay: 0.3 }}
                >
                  Aucun document disponible.
                </motion.p>
              ) : (
                <ul style={{ listStyle: "none", padding: 0 }}>
                  {documents.map((doc, idx) => (
                    <motion.li
                      key={doc.id}
                      style={{
                        display: "flex",
                        alignItems: "center",
                        justifyContent: "space-between",
                        padding: "8px 0",
                        borderBottom: "1px solid #eee",
                      }}
                      initial={{ opacity: 0, y: 20 }}
                      animate={{ opacity: 1, y: 0 }}
                      transition={{ delay: 0.4 + idx * 0.05, duration: 0.4 }}
                    >
                      <span>{doc.name}</span>
                      <span>
                        <span
                          style={{
                            cursor: "pointer",
                            marginRight: 16,
                            color: "#007bff",
                            fontSize: 18,
                          }}
                          title="Éditer"
                          onClick={() => handleEdit(doc.id)}
                          role="img"
                          aria-label="edit"
                        >
                          ✏️
                        </span>
                        <span
                          style={{
                            cursor: "pointer",
                            color: "#dc3545",
                            fontSize: 18,
                          }}
                          title="Supprimer"
                          onClick={() => handleDelete(doc.id)}
                          role="img"
                          aria-label="delete"
                        >
                          🗑️
                        </span>
                      </span>
                    </motion.li>
                  ))}
                </ul>
              )}
            </motion.div>
          )}
        </AnimatePresence>
      </main>
    </motion.div>
  );
};

export default Documents;
