              setFormData(prev => ({
                ...prev,
                latitude: '',
                longitude: ''
              }));
              enableMapClick(); // R√©active le clic sur la carte
            });
          }

          disableMapClick(); // D√©sactive le clic sur la carte apr√®s s√©lection
          console.log(`Position s√©lectionn√©e: ${lat.toFixed(6)}, ${lng.toFixed(6)}`);
        };

        enableMapClick();

        setTimeout(() => {
          if (leafletMapRef.current) {
            leafletMapRef.current.invalidateSize();
          }
        }, 200);

        setMapInitialized(true);
        console.log("‚úÖ Carte initialis√©e avec succ√®s");
