import { useState } from 'react';
import {
    Users,
    Key,
    BarChart3,
    File,
    LogOut
} from 'lucide-react';

import '../css/AdminDashboard.css';

// Barre de navigation latérale (Sidebar)
const Sidebar = ({ activeTab, setActiveTab }) => {
    return (
        <nav className="sidebar">
            <div className="sidebar-header">
                <span className="sidebar-title">Admin</span>
            </div>
            <ul className="sidebar-menu">
                <li
                    className={activeTab === 'dashboard' ? 'active' : ''}
                    onClick={() => setActiveTab('dashboard')}
                >
                    <BarChart3 size={18} /> <span>Dashboard</span>
                </li>
                <li
                    className={activeTab === 'users' ? 'active' : ''}
                    onClick={() => setActiveTab('users')}
                >
                    <Users size={18} /> <span>Utilisateurs</span>
                </li>
                <li
                    className={activeTab === 'passwords' ? 'active' : ''}
                    onClick={() => setActiveTab('passwords')}
                >
                    <Key size={18} /> <span>Mots de passe</span>
                </li>
                <li
                    className={activeTab === 'documents' ? 'active' : ''}
                    onClick={() => setActiveTab('documents')}
                >
                    <File size={18} /> <span>Documents</span>
                </li>
                <li
                    className={activeTab === 'logout' ? 'active' : ''}
                    onClick={() => setActiveTab('logout')}
                >
                    <LogOut size={18} /> <span>Déconnexion</span>
                </li>
            </ul>
        </nav>
    );
};

const AdminDashboard = () => {
    const [activeTab, setActiveTab] = useState('dashboard');

    return (
        <div className="admin-dashboard">
            <Sidebar activeTab={activeTab} setActiveTab={setActiveTab} />
            <div className="main-content">
                {/* Zone de contenu à compléter selon l'onglet sélectionné */}
                <div className="tab-content">
                    {/* Ajoutez ici le contenu pour chaque onglet */}
                </div>
            </div>
        </div>
    );
};

export default AdminDashboard;
