import React, { useRef, useState } from "react";
import RessourceCategory from "../Home/ResourceCategory";

const Documents = () => {
    const [dragActive, setDragActive] = useState(false);
    const inputRef = useRef(null);

    // À adapter selon votre logique métier
    const type = "documents";
    const items = []; // Remplacez par vos données réelles
    const API_BASE_URL = "/api/documents"; // À adapter

    const handleDrag = (e) => {
        e.preventDefault();
        e.stopPropagation();
        if (e.type === "dragenter" || e.type === "dragover") {
            setDragActive(true);
        } else if (e.type === "dragleave") {
            setDragActive(false);
        }
    };

    const handleDrop = (e) => {
        e.preventDefault();
        e.stopPropagation();
        setDragActive(false);
        if (e.dataTransfer.files && e.dataTransfer.files[0]) {
            // Gérer les fichiers ici
            console.log(e.dataTransfer.files);
        }
    };

    const handleChange = (e) => {
        if (e.target.files && e.target.files[0]) {
            // Gérer les fichiers ici
            console.log(e.target.files);
        }
    };

    return (
        <>
            <div>
                <h2>Documents</h2>
            </div>
            <div>
                <form
                    onDragEnter={handleDrag}
                    onSubmit={(e) => e.preventDefault()}
                    style={{ position: "relative" }}
                >
                    <input
                        ref={inputRef}
                        type="file"
                        multiple
                        style={{ display: "none" }}
                        onChange={handleChange}
                    />
                    <div
                        onClick={() => inputRef.current.click()}
                        onDragEnter={handleDrag}
                        onDragLeave={handleDrag}
                        onDragOver={handleDrag}
                        onDrop={handleDrop}
                        style={{
                            border: "2px dashed #aaa",
                            borderRadius: "8px",
                            padding: "40px",
                            textAlign: "center",
                            background: dragActive ? "#f0f8ff" : "#fff",
                            cursor: "pointer",
                            transition: "background 0.2s",
                        }}
                    >
                        <p>Déposez vos fichiers ici ou cliquez pour sélectionner</p>
                    </div>
                </form>
                <RessourceCategory type={type} items={items} apiUrl={API_BASE_URL} />
            </div>
        </>
    );
};

export default Documents;
