import React, { useEffect, useState, useRef } from "react";
import axios from "axios";

const Documents = () => {
  const [documents, setDocuments] = useState([]);
  const [uploading, setUploading] = useState(false);
  const API_BASE_URL = process.env.REACT_APP_API_URL;
  const fileInputRef = useRef(null);

  useEffect(() => {
    fetchDocuments();
    // eslint-disable-next-line
  }, [API_BASE_URL]);

  const fetchDocuments = async () => {
    try {
      const res = await axios.get(`${API_BASE_URL}/admin/documents`);
      setDocuments(res.data);
    } catch (err) {
      console.error("Erreur lors de la r√©cup√©ration des documents", err);
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Supprimer ce document ?")) return;
    try {
      await axios.delete(`${API_BASE_URL}/admin/documents/${id}`);
      setDocuments((docs) => docs.filter((doc) => doc.id !== id));
    } catch (err) {
      alert("Erreur lors de la suppression");
    }
  };

  const handleEdit = (id) => {
    alert("Fonction d'√©dition √† impl√©menter pour l'id: " + id);
  };

  const handleDrop = async (e) => {
    e.preventDefault();
    const file = e.dataTransfer.files[0];
    if (!file) return;
    await uploadFile(file);
  };

  const handleFileChange = async (e) => {
    const file = e.target.files[0];
    if (!file) return;
    await uploadFile(file);
  };

  const uploadFile = async (file) => {
    setUploading(true);
    const formData = new FormData();
    formData.append("document", file);
    try {
      await axios.post(`${API_BASE_URL}/admin/documents`, formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      fetchDocuments();
    } catch (err) {
      alert("Erreur lors de l'upload");
    } finally {
      setUploading(false);
    }
  };

  return (
    <div>
      <h1>Documents Administrateurs</h1>
      <div
        style={{
          border: "2px dashed #aaa",
          borderRadius: 8,
          padding: 24,
          textAlign: "center",
          marginBottom: 24,
          background: "#fafafa",
          cursor: "pointer",
          position: "relative",
        }}
        onDrop={handleDrop}
        onDragOver={(e) => e.preventDefault()}
        onClick={() => fileInputRef.current.click()}
      >
        <span style={{ fontSize: 32, marginBottom: 8, display: "inline-block" }}>üì§</span>
        <div>
          {uploading ? "Chargement..." : "D√©posez un fichier ici ou cliquez pour ajouter"}
        </div>
        <input
          type="file"
          ref={fileInputRef}
          style={{ display: "none" }}
          onChange={handleFileChange}
        />
      </div>
      <ul style={{ listStyle: "none", padding: 0 }}>
        {documents.map((doc) => (
          <li
            key={doc.id}
            style={{
              display: "flex",
              alignItems: "center",
              justifyContent: "space-between",
              padding: "8px 0",
              borderBottom: "1px solid #eee",
            }}
          >
            <span>{doc.name}</span>
            <span>
              <span
                style={{ cursor: "pointer", marginRight: 16, color: "#007bff", fontSize: 18 }}
                title="√âditer"
                onClick={() => handleEdit(doc.id)}
                role="img"
                aria-label="edit"
              >
                ‚úèÔ∏è
              </span>
              <span
                style={{ cursor: "pointer", color: "#dc3545", fontSize: 18 }}
                title="Supprimer"
                onClick={() => handleDelete(doc.id)}
                role="img"
                aria-label="delete"
              >
                üóëÔ∏è
              </span>
            </span>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Documents;
