import { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import '../css/carte.css';
import React from "react";
import Tesseract from "tesseract.js";

// Fix Leaflet marker icon issue
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png',
  iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
});

const Carte = () => {
  const [lieux, setLieux] = useState([]);
  const [rapportTitres, setRapportTitres] = useState({});
  const mapRef = useRef(null);
  const mapContainerRef = useRef(null);
  const [pointActuel, setPointActuel] = useState(null);
  const markerRefs = useRef([]);
  const gifOverlayRef = useRef(null);
  const [pendingGif, setPendingGif] = useState(null);

  // Suppression de la saisie manuelle
  // const [manualCoords, setManualCoords] = useState({ lat: '', lng: '' });

  // Ajout pour coordonnées extraites par Tesseract
  const [parsedCoords, setParsedCoords] = useState({ lat: '', lng: '' });

  const API_BASE_URL = process.env.REACT_APP_API_URL;

  // Fonction utilitaire pour convertir le format D° M' en décimal
  function dmsToDecimal(dmsStr) {
    // Ex: "-001 ° 05,79'"
    const match = dmsStr.match(/(-?\d+)\s*[°]\s*(\d+)[,\.](\d+)'?/);
    if (!match) return null;
    const deg = parseInt(match[1], 10);
    const min = parseInt(match[2], 10);
    const sec = parseInt(match[3], 10);
    const minutes = parseFloat(`${min}.${sec}`);
    const decimal = Math.abs(deg) + (minutes / 60);
    return deg < 0 ? -decimal : decimal;
  }

  // Fonction pour parser le GIF et extraire les coordonnées avec Tesseract
  const parseGifForCoords = async (gifDataUrl) => {
    return new Promise((resolve) => {
      const img = document.createElement("img");
      img.src = gifDataUrl;
      img.onload = async () => {
        const canvas = document.createElement("canvas");
        canvas.width = img.width;
        canvas.height = img.height;
        const ctx = canvas.getContext("2d");
        ctx.drawImage(img, 0, 0);
        const dataUrl = canvas.toDataURL("image/png");
        const { data: { text } } = await Tesseract.recognize(dataUrl, "eng");

        // Extraction latitude au format D° M'
        let latMatchDMS = text.match(/(-?\d{2,3}\s*[°]\s*\d{2}[,\.]\d{2,})/);
        let lat = "";
        if (latMatchDMS) {
          lat = dmsToDecimal(latMatchDMS[1]);
        } else {
          // Fallback sur l'ancien format décimal
          let latMatchDec = text.match(/Lat(?:itude)?[:\s]*([-\d.]+)/i);
          lat = latMatchDec ? parseFloat(latMatchDec[1]) : "";
        }

        // Extraction longitude au format D° M'
        let lonMatchDMS = text.match(/(-?\d{3}\s*[°]\s*\d{2}[,\.]\d{2,})/);
        let lng = "";
        if (lonMatchDMS) {
          lng = dmsToDecimal(lonMatchDMS[1]);
        } else {
          // Fallback sur l'ancien format décimal
          let lonMatchDec = text.match(/Lon(?:gitude)?[:\s]*([-\d.]+)/i);
          lng = lonMatchDec ? parseFloat(lonMatchDec[1]) : "";
        }

        resolve({
          lat: lat !== null && lat !== "" ? lat : "",
          lng: lng !== null && lng !== "" ? lng : "",
        });
      };
    });
  };

  // Gère la sélection d'un fichier GIF par l'utilisateur
  const handleGifUpload = async (event) => {
    const file = event.target.files[0];
    if (!file || !file.name.endsWith('.gif')) return;

    // Utilise FileReader pour lire le fichier GIF en base64
    const reader = new FileReader();
    reader.onload = async function (e) {
      setPendingGif(e.target.result); // Stocke le GIF en attente de validation
      // Appelle le parser Tesseract et met à jour les coordonnées extraites
      const coords = await parseGifForCoords(e.target.result);
      setParsedCoords(coords);
    };
