import { useState } from 'react';
import {
    Users,
    Key,
    BarChart3,
    File
} from 'lucide-react';

import '../css/AdminDashboard.css';

const NAV_TABS = [
    { key: 'dashboard', label: 'Tableau de bord', icon: BarChart3 },
    { key: 'passwords', label: 'Mots de passe', icon: Key },
    { key: 'operateurs', label: 'Opérateurs', icon: Users },
    { key: 'documents', label: 'Documents', icon: File }
];

const AdminDashboard = () => {
    const [activeTab, setActiveTab] = useState('dashboard');

    return (
        <div className="admin-dashboard">
            <nav className="admin-sidebar">
                <ul>
                    {NAV_TABS.map(tab => (
                        <li
                            key={tab.key}
                            className={activeTab === tab.key ? 'active' : ''}
                            onClick={() => setActiveTab(tab.key)}
                        >
                            <tab.icon size={18} style={{ marginRight: 8 }} />
                            {tab.label}
                        </li>
                    ))}
                </ul>
            </nav>
            <div className="main-content">
                {activeTab === 'dashboard' && (
                    <div className="admin-placeholder">
                        {/* Zone à remplir : Tableau de bord */}
                        <h2>Tableau de bord</h2>
                        <div className="placeholder-box">Contenu du tableau de bord ici...</div>
                    </div>
                )}
                {activeTab === 'passwords' && (
                    <div className="admin-placeholder">
                        {/* Zone à remplir : Gestion des mots de passe */}
                        <h2>Gestion des mots de passe</h2>
                        <div className="placeholder-box">Contenu de gestion des mots de passe ici...</div>
                    </div>
                )}
                {activeTab === 'operateurs' && (
                    <div className="admin-placeholder">
                        {/* Zone à remplir : Liste des opérateurs */}
                        <h2>Liste des opérateurs</h2>
                        <div className="placeholder-box">Contenu de la liste des opérateurs ici...</div>
                    </div>
                )}
                {activeTab === 'documents' && (
                    <div className="admin-placeholder">
                        {/* Zone à remplir : Liste des documents */}
                        <h2>Liste des documents</h2>
                        <div className="placeholder-box">Contenu de la liste des documents ici...</div>
                    </div>
                )}
            </div>
        </div>
    );
};

export default AdminDashboard;
