import React, { useEffect, useState } from "react";
import axios from "axios";

const Password = () => {
    const [passwords, setPasswords] = useState([]);
    const [visibleIds, setVisibleIds] = useState([]);
    const [editId, setEditId] = useState(null);
    const [editValue, setEditValue] = useState("");

    const API_BASE_URL = process.env.REACT_APP_API_URL;

    const fetchPasswords = async () => {
        try {
            const res = await axios.get(`${API_BASE_URL}/admin/motdepasse`);
            setPasswords(res.data);
        } catch (err) {
            console.error("Erreur lors de la récupération des mots de passe", err);
        }
    };

    useEffect(() => {
        fetchPasswords();
    }, []);

    const toggleVisibility = (id) => {
        setVisibleIds((prev) =>
            prev.includes(id) ? prev.filter((v) => v !== id) : [...prev, id]
        );
    };

    const handleEdit = (id, value) => {
        setEditId(id);
        setEditValue(value);
    };

    const handleEditChange = (e) => {
        setEditValue(e.target.value);
    };

    const saveEdit = async (id) => {
        try {
            await axios.put(
                `${API_BASE_URL}/admin/motdepasse/${id}`,
                { motdepasse: editValue }
            );
            setPasswords((prev) =>
                prev.map((item) =>
                    item.id === id ? { ...item, motdepasse: editValue } : item
                )
            );
            setEditId(null);
            setEditValue("");
        } catch (err) {
            console.error("Erreur lors de la modification du mot de passe", err);
        }
    };

    return (
        <div className="passwords-container">
            <ul className="passwords-list">
                {[...new Set(passwords.map((item) => item.mot_de_passe))].map(
                    (motDePasse) => (
                        <React.Fragment key={motDePasse}>
                            <h2>{motDePasse}</h2>
                            {passwords
                                .filter((item) => item.mot_de_passe === motDePasse)
                                .map((item) => (
                                    <li key={item.id} className="password-item">
                                        {editId === item.id ? (
                                            <div className="password-edit-row">
                                                <input
                                                    className="password-input"
                                                    type="text"
                                                    value={editValue}
                                                    onChange={handleEditChange}
                                                />
                                                <button
                                                    className="password-btn password-btn-save"
                                                    onClick={() => saveEdit(item.id)}
                                                >
                                                    Enregistrer
                                                </button>
                                                <button
                                                    className="password-btn password-btn-cancel"
                                                    onClick={() => setEditId(null)}
                                                >
                                                    Annuler
                                                </button>
                                            </div>
                                        ) : (
                                            <div className="password-row">
                                                <span className="password-value">
                                                    {visibleIds.includes(item.id)
                                                        ? item.mot_de_passe
                                                        : "••••••••"}
                                                </span>
                                                <button
                                                    className="password-btn password-btn-toggle"
                                                    onClick={() => toggleVisibility(item.id)}
                                                >
                                                    {visibleIds.includes(item.id)
                                                        ? "Masquer"
                                                        : "Afficher"}
                                                </button>
                                                <button
                                                    className="password-btn password-btn-edit"
                                                    style={{ marginLeft: 8 }}
                                                    onClick={() =>
                                                        handleEdit(item.id, item.mot_de_passe)
                                                    }
                                                >
                                                    Éditer
                                                </button>
                                            </div>
                                        )}
                                    </li>
                                ))}
                        </React.Fragment>
                    )
                )}
            </ul>
        </div>
    );
};

export default Password;
